CREATE OR REPLACE PROCEDURE duplicar_modelo_sae ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;	
nr_seq_item_w	bigint;	
nr_seq_mod_item_w	bigint;		

c01 CURSOR FOR
	SELECT	*
	from	pe_sae_modelo
	where	nr_sequencia	= nr_sequencia_p;
	
c03 CURSOR FOR
	SELECT	*
	from	pe_sae_mod_item
	where	nr_seq_modelo	= nr_sequencia_p;

c01_w c01%rowtype;
c03_w c03%rowtype;


BEGIN
open c01;
loop
fetch c01 into	
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	nextval('pe_sae_modelo_seq')
	into STRICT	nr_sequencia_w
	;
	
	insert into pe_sae_modelo( 	nr_sequencia, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, ds_modelo, 
					ds_utilizacao, ie_situacao, ie_pep, 
					ie_pepo ) 
		values ( 	nr_sequencia_w, clock_timestamp(), nm_usuario_p, 
					clock_timestamp(), nm_usuario_p, substr(obter_desc_expressao(303214)||' '||c01_w.ds_modelo,1,80), 
					c01_w.ds_utilizacao, c01_w.ie_situacao, c01_w.ie_pep, 
					c01_w.ie_pepo );
					
					
	insert into pe_sae_modelo_perfil(
						nr_sequencia, 
						dt_atualizacao, 
						nm_usuario, 
						dt_atualizacao_nrec, 
						nm_usuario_nrec, 
						nr_seq_pe_sae_model, 
						cd_perfil, 
						ie_inserir, 
						ie_visualizacao_excl, 
						cd_setor_Atendimento,
						cd_estabelecimento)					
				SELECT	nextval('pe_sae_modelo_perfil_seq'), 
						clock_timestamp(), 
						nm_usuario_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						nr_sequencia_w, 
						cd_perfil, 
						ie_inserir, 
						ie_visualizacao_excl, 
						cd_setor_Atendimento,
						cd_estabelecimento
				from	pe_sae_modelo_perfil
				where	nr_seq_pe_sae_model	= 	nr_sequencia_p;
	
	open c03;
	loop
	fetch c03 into	
		c03_w;
	EXIT WHEN NOT FOUND; /* apply on c03 */
		begin
		select	nextval('pe_sae_mod_item_seq')
		into STRICT	nr_seq_item_w
		;
		nr_seq_mod_item_w	:= c03_w.nr_sequencia;
		insert into pe_sae_mod_item(
		nr_sequencia, dt_atualizacao, nm_usuario, 
		dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_modelo, 
		nr_seq_item, cd_setor_pac,IE_OBRIGATORIO) 
		 values ( 
		nr_seq_item_w, clock_timestamp(), nm_usuario_p, 
		clock_timestamp(), nm_usuario_p, nr_sequencia_w, 
		c03_w.nr_seq_item, c03_w.cd_setor_pac,coalesce(c03_w.IE_OBRIGATORIO,'N'));
		
		insert into pe_sae_mod_item_result(
							nr_sequencia, 
							dt_atualizacao, 
							nm_usuario,
							dt_atualizacao_nrec, 
							nm_usuario_nrec, 
							nr_seq_item,
							nr_seq_resultado ) 
					SELECT	nextval('pe_sae_mod_item_result_seq'), 
							clock_timestamp(), 
							nm_usuario_p, 
							clock_timestamp(), 
							nm_usuario_p, 
							nr_seq_item_w, 
							nr_seq_resultado
					from	pe_sae_mod_item_result
					where	nr_seq_item	= nr_seq_mod_item_w;
		end;
	end loop;
	close c03;				
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
