SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplicar_nut_orientacao ( nm_usuario_p text, nr_sequencia_p bigint ) AS $body$
DECLARE


ie_param103_w 		varchar(1);
cd_estabelecimento_w	smallint;


BEGIN

cd_estabelecimento_w:= wheb_usuario_pck.get_cd_estabelecimento;

ie_param103_w := obter_param_usuario(1003, 103, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_param103_w);

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	insert into nut_orientacao(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_inicio,
		dt_final,
		nr_seq_servico,
		cd_pessoa_fisica,
		ie_situacao,
		ds_orientacao,
		dt_liberacao
		)
		SELECT	nextval('nut_orientacao_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			CASE WHEN ie_param103_w='N' THEN clock_timestamp()  ELSE dt_inicio END ,
			CASE WHEN ie_param103_w='N' THEN null  ELSE dt_final END ,
			nr_seq_servico,
			cd_pessoa_fisica,
			'A',
			ds_orientacao,
			null
		from	nut_orientacao
		where	nr_sequencia	= nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
