CREATE OR REPLACE PROCEDURE duplicar_ordem_transf ( nr_ordem_compra_p bigint, nm_usuario_p text, nr_ordem_nova_p INOUT bigint) AS $body$
DECLARE

 
nr_ordem_compra_w	bigint;
nr_item_oci_w 		integer;
qt_material_w 		double precision;
dt_prevista_entrega_w	timestamp;

C01 CURSOR FOR 
	SELECT	nr_item_oci, 
		qt_material 
	from	ordem_compra_item 
	where	nr_ordem_compra = nr_ordem_compra_p;


BEGIN 
select	nextval('ordem_compra_seq') 
into STRICT	nr_ordem_compra_w
;
 
insert into ordem_compra( 
	nr_ordem_compra, 
	cd_estabelecimento, 
	cd_cgc_fornecedor, 
	cd_condicao_pagamento, 
	cd_comprador, 
	dt_ordem_compra, 
	dt_atualizacao, 
	nm_usuario, 
	cd_moeda, 
	ie_situacao, 
	dt_inclusao, 
	cd_pessoa_solicitante, 
	cd_cgc_transportador, 
	ie_frete, 
	vl_frete, 
	pr_multa_atraso, 
	pr_desconto, 
	pr_desc_pgto_antec, 
	pr_juros_negociado, 
	dt_emissao, 
	ds_pessoa_contato, 
	ds_observacao, 
	cd_motivo_alteracao, 
	cd_local_entrega, 
	dt_entrega, 
	nr_prescricao, 
	ie_aviso_chegada, 
	nr_requisicao, 
	cd_pessoa_fisica, 
	nr_ordem_agrup, 
	ie_emite_obs, 
	ie_urgente, 
	nr_seq_interno, 
	nr_atendimento, 
	ie_somente_pagto, 
	vl_despesa_acessoria, 
	cd_setor_atendimento, 
	nr_seq_subgrupo_compra, 
	pr_desc_financeiro, 
	nr_seq_forma_pagto, 
	vl_desconto, 
	ie_tipo_ordem, 
	cd_estab_transf, 
	cd_local_transf) 
(SELECT 
	nr_ordem_compra_w, 
	cd_estabelecimento, 
	cd_cgc_fornecedor, 
	cd_condicao_pagamento, 
	cd_comprador, 
	clock_timestamp(), 
	clock_timestamp(), 
	nm_usuario_p, 
	cd_moeda, 
	ie_situacao, 
	dt_inclusao, 
	cd_pessoa_solicitante, 
	cd_cgc_transportador, 
	ie_frete, 
	vl_frete, 
	pr_multa_atraso, 
	pr_desconto, 
	pr_desc_pgto_antec, 
	pr_juros_negociado, 
	dt_emissao, 
	ds_pessoa_contato, 
	substr(Wheb_mensagem_pck.get_Texto(299734, 'NR_ORDEM_COMPRA_P='||NR_ORDEM_COMPRA_P),1,100), /*Gerada a partir da solicitação de transferência de estoque #@NR_ORDEM_COMPRA_P#@.*/
 
	cd_motivo_alteracao, 
	cd_local_entrega, 
	dt_entrega, 
	nr_prescricao, 
	ie_aviso_chegada, 
	nr_requisicao, 
	cd_pessoa_fisica, 
	nr_ordem_agrup, 
	ie_emite_obs, 
	ie_urgente, 
	nr_seq_interno, 
	nr_atendimento, 
	ie_somente_pagto, 
	vl_despesa_acessoria, 
	cd_setor_atendimento, 
	nr_seq_subgrupo_compra, 
	pr_desc_financeiro, 
	nr_seq_forma_pagto, 
	coalesce(vl_desconto,0), 
	ie_tipo_ordem, 
	cd_estab_transf, 
	cd_local_transf 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p);
 
open C01;
loop 
fetch C01 into	 
	nr_item_oci_w, 
	qt_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	insert into ordem_compra_item( 
		nr_ordem_compra, 
		nr_item_oci, 
		cd_material, 
		cd_unidade_medida_compra, 
		vl_unitario_material, 
		qt_material, 
		dt_atualizacao, 
		nm_usuario, 
		ie_situacao, 
		cd_pessoa_solicitante, 
		pr_descontos, 
		cd_local_estoque, 
		ds_material_direto, 
		ds_observacao, 
		cd_motivo_alteracao, 
		ds_marca, 
		vl_item_liquido, 
		cd_centro_custo, 
		cd_conta_contabil, 
		qt_conv_unid_fornec, 
		nr_seq_proj_rec, 
		pr_desc_financ, 
		nr_seq_conta_financ, 
		qt_original, 
		dt_validade, 
		nr_seq_marca, 
		vl_ultima_compra, 
		vl_dif_ultima_compra, 
		pr_dif_ultima_compra, 
		vl_desconto, 
		vl_total_item) 
	(SELECT	nr_ordem_compra_w, 
		nr_item_oci, 
		cd_material, 
		cd_unidade_medida_compra, 
		vl_unitario_material, 
		qt_material, 
		dt_atualizacao, 
		nm_usuario, 
		ie_situacao, 
		cd_pessoa_solicitante, 
		pr_descontos, 
		cd_local_estoque, 
		ds_material_direto, 
		ds_observacao, 
		cd_motivo_alteracao, 
		ds_marca, 
		vl_item_liquido, 
		cd_centro_custo, 
		cd_conta_contabil, 
		qt_conv_unid_fornec, 
		nr_seq_proj_rec, 
		pr_desc_financ, 
		nr_seq_conta_financ, 
		qt_material, 
		dt_validade, 
		nr_seq_marca, 
		vl_ultima_compra, 
		vl_dif_ultima_compra, 
		pr_dif_ultima_compra, 
		coalesce(vl_desconto,0), 
		round((qt_material * vl_unitario_material)::numeric,4) 
	from	ordem_compra_item 
	where	nr_ordem_compra = nr_ordem_compra_p 
	and	nr_item_oci = nr_item_oci_w);
	 
	select 	max(dt_prevista_entrega) 
	into STRICT	dt_prevista_entrega_w 
	from	ordem_compra_item_entrega 
	where	nr_ordem_compra = nr_ordem_compra_p 
	and	nr_item_oci = nr_item_oci_w;
	 
	insert into ordem_compra_item_entrega( 
		nr_sequencia, 
		nr_ordem_compra, 
		nr_item_oci, 
		dt_prevista_entrega, 
		qt_prevista_entrega, 
		dt_atualizacao, 
		nm_usuario) 
	values (nextval('ordem_compra_item_entrega_seq'), 
		nr_ordem_compra_w, 
		nr_item_oci_w, 
		dt_prevista_entrega_w, 
		qt_material_w, 
		clock_timestamp(), 
		nm_usuario_p);
	end;
end loop;
close C01;
 
nr_ordem_nova_p	:= nr_ordem_compra_w;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
