CREATE OR REPLACE PROCEDURE duplicar_pacote_regra_matproc ( nr_seq_regra_p bigint, ie_pasta_p bigint) AS $body$
DECLARE


nr_seq_regra_w		bigint;


BEGIN

if (ie_pasta_p = 62) then

	select	nextval('pacote_material_seq')
	into STRICT	nr_seq_regra_w
	;
	
	insert into pacote_material(
		nr_seq_pacote,
		nr_sequencia,
		ie_inclui_exclui,
		dt_atualizacao,
		nm_usuario,
		ie_excedente,
		cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material,
		cd_material,
		qt_limite,
		ie_tipo_atendimento,
		cd_setor_atendimento,
		nr_seq_pac_acomod,
		ie_valor,
		vl_minimo,
		vl_maximo,
		pr_desconto,
		qt_idade_min,
		qt_idade_max,
		ie_objetivo,
		ie_tipo_setor,
		ie_considera_generico,
		pr_orcamento,
		nr_seq_familia,
		ie_sexo,
		ie_somente_sem_rla,
		cd_centro_custo,
		ie_ratear_item,
		vl_negociado,
		nr_seq_estrutura,
		cd_local_estoque)
	SELECT	nr_seq_pacote,
		nr_seq_regra_w,
		ie_inclui_exclui,
		dt_atualizacao,
		nm_usuario,
		ie_excedente,
		cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material,
		cd_material,
		qt_limite,
		ie_tipo_atendimento,
		cd_setor_atendimento,
		nr_seq_pac_acomod,
		ie_valor,
		vl_minimo,
		vl_maximo,
		pr_desconto,
		qt_idade_min,
		qt_idade_max,
		ie_objetivo,
		ie_tipo_setor,
		ie_considera_generico,
		pr_orcamento,
		nr_seq_familia,
		ie_sexo,
		ie_somente_sem_rla,
		cd_centro_custo,
		ie_ratear_item,
		vl_negociado,
		nr_seq_estrutura,
		cd_local_estoque
	from	pacote_material
	where	nr_sequencia = nr_seq_regra_p;
	
	insert into pacote_material_adic(
		nr_seq_pacote,
		nr_seq_mat,
		cd_material,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	SELECT	nr_seq_pacote,
		nr_seq_regra_w,
		cd_material,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec
	from	pacote_material_adic
	where	nr_seq_mat = nr_seq_regra_p;
	
elsif (ie_pasta_p = 63) then
	
	insert into pacote_procedimento(
		nr_seq_pacote,
		nr_sequencia,
		ie_inclui_exclui,
		dt_atualizacao,
		nm_usuario,
		ie_excedente,
		cd_area_proced,
