		nr_seq_pac_acomod,
		pr_desconto,
		qt_idade_min,
		qt_idade_max,
		vl_maximo,
		vl_minimo,
		ie_tipo_valor,
		nr_seq_proc_interno,
		ie_ratear_item,
		pr_desc,
		vl_negociado,
		ie_agendavel,
		ie_sexo,
		cd_centro_custo,
		nr_seq_classif,
		nr_seq_estrutura)
	SELECT	nr_seq_pacote,
		nextval('pacote_procedimento_seq'),
		ie_inclui_exclui,
		dt_atualizacao,
		nm_usuario,
		ie_excedente,
		cd_area_proced,
		cd_especial_proced,
		cd_grupo_proced,
		cd_procedimento,
		ie_origem_proced,
		qt_limite,
		ie_considera_honorario,
		cd_setor_atendimento,
