SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplicar_parecer_medico ( nm_usuario_p text, nr_seq_parecer_p bigint, ie_parecer_medico_p text, nr_seq_opiniao_p bigint default null) AS $body$
DECLARE


nr_seq_parecer_w 		parecer_medico_req.nr_parecer%type;
nr_sequencia_w			parecer_medico.nr_sequencia%type;
nr_seq_interno_w		parecer_medico.nr_seq_interno%type;
nr_seq_interno_orig_w	parecer_medico.nr_seq_interno%type;


BEGIN

	if (ie_parecer_medico_p = 'S') THEN
		nr_seq_parecer_w := nr_seq_parecer_p;

		nr_seq_parecer_w := duplicar_registro('PARECER_MEDICO_REQ', nm_usuario_p, nr_seq_parecer_w);

		update  parecer_medico_req
		set  	dt_liberacao  = NULL,
				dt_inativacao  = NULL,
				nm_usuario_inativacao  = NULL,
				ds_justificativa  = NULL,
				ie_situacao = 'A',
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
		where 	nr_parecer = nr_seq_parecer_w;

		commit;
	else

		select max(nr_sequencia) + 1
		into STRICT nr_sequencia_w
		from parecer_medico 
		where nr_parecer  = nr_seq_parecer_p;

		select	nextval('parecer_medico_seq')
		into STRICT	nr_seq_interno_w
		;
		
		select	max(nr_seq_interno)
		into STRICT	nr_seq_interno_orig_w
		from	parecer_medico
		where  	nr_parecer  = nr_seq_parecer_p
		and  	nr_sequencia = nr_seq_opiniao_p;

		insert into parecer_medico(nr_parecer,
									 nr_sequencia,
									 cd_medico,
									 dt_atualizacao,
									 nm_usuario,			
									 dt_liberacao,
									 ie_situacao,
									 dt_inativacao,
									 nm_usuario_inativacao,
									 ds_justificativa,
									 nr_seq_assinatura,
									 nr_seq_assinat_inativacao,
									 ie_status,
									 nr_seq_interno,
									 ds_utc,
									 ie_horario_verao,
									 ds_utc_atualizacao,
									 nr_seq_regulacao,
									 nr_seq_avaliacao)
						   SELECT  	 nr_parecer,
									 nr_sequencia_w,
									 cd_medico,
									 dt_atualizacao,
									 nm_usuario,			 
									 NULL,
									 'A',
									 NULL,
									 NULL,
									 NULL,
									 nr_seq_assinatura,
									 nr_seq_assinat_inativacao,
									 ie_status,
									 nr_seq_interno_w,
									 ds_utc,
									 ie_horario_verao,
									 ds_utc_atualizacao,
									 nr_seq_regulacao,
									 nr_seq_avaliacao
						   from  	 parecer_medico
						   where   	 nr_parecer  = nr_seq_parecer_p
						   and  	 nr_sequencia = nr_seq_opiniao_p;
		
		   CALL copia_campo_long_de_para('PARECER_MEDICO',
					'DS_PARECER',
					'where ' || 'nr_seq_interno' || ' = :nr_seq_interno ', 					
					'nr_seq_interno=' || nr_seq_interno_orig_w,
					'PARECER_MEDICO',
					'DS_PARECER',
					'where ' || 'nr_seq_interno' || ' = :nr_seq_interno ',
					'nr_seq_interno=' || nr_seq_interno_w );
		
		   
		commit;
	end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
