CREATE OR REPLACE PROCEDURE duplicar_participante_cir ( cd_participante_p text, nm_participante_p text, nr_cirurgia_p bigint, ie_funcao_p text, nm_usuario_p text) AS $body$
DECLARE




nr_sequencia_w		bigint;



BEGIN


if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
select	coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_sequencia_w
from 	cirurgia_participante
where	nr_cirurgia	=	nr_cirurgia_p;


	insert into cirurgia_participante(
		nr_cirurgia,
		nr_sequencia,
		ie_funcao,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		cd_pessoa_fisica,
		nm_participante,
		nr_seq_interno,
		ie_situacao)
	values (
		nr_cirurgia_p,
		nr_sequencia_w,
		ie_funcao_p,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		cd_participante_p,
		nm_participante_p,
		nextval('cirurgia_participante_seq'),
		'A');
end if;

commit;

end;
$body$
