CREATE OR REPLACE PROCEDURE duplicar_part_cir_html ( nr_sequencia_list_p text, nr_cirurgia_p bigint) AS $body$
DECLARE


vSQL      text;
vCursor   REFCURSOR;

nr_sequencia_w      bigint;
nm_usuario_w		varchar(50);
cd_pessoa_fisica_w  cirurgia_participante.cd_pessoa_fisica%type;
nm_participante_w   cirurgia_participante.nm_participante%type;
nr_cirurgia_w		cirurgia_participante.nr_cirurgia%type;
ie_funcao_w			cirurgia_participante.ie_funcao%type;


BEGIN

nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

vSQL := 'SELECT	cd_pessoa_fisica,     ' ||
        ' 			nm_participante,  ' ||
        '			nr_cirurgia,	  ' ||
        '			ie_funcao		  ' ||
        '	FROM 	cirurgia_participante ' ||
        '	WHERE	nr_cirurgia	= ' || nr_cirurgia_p ||
        '	AND 	nr_sequencia IN (' || nr_sequencia_list_p || ')';

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') and (nr_sequencia_list_p IS NOT NULL AND nr_sequencia_list_p::text <> '')then

--Abre o cursor com o select da vari√°vel
Open vCursor for EXECUTE vSQL;
  Loop
  Fetch vCursor into cd_pessoa_fisica_w, nm_participante_w, nr_cirurgia_w, ie_funcao_w;
  EXIT WHEN NOT FOUND; /* apply on vCursor  */

	select	coalesce(max(nr_sequencia),0) + 1
	  into STRICT	nr_sequencia_w
	  from 	cirurgia_participante
	 where	nr_cirurgia	=	nr_cirurgia_p;

	insert into cirurgia_participante(
		nr_cirurgia,
		nr_sequencia,
		ie_funcao,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		cd_pessoa_fisica,
		nm_participante,
		nr_seq_interno,
		ie_situacao)
	values (
		nr_cirurgia_p,
		nr_sequencia_w,
		ie_funcao_w,
		coalesce(nm_usuario_w, ''),
		clock_timestamp(),
		coalesce(nm_usuario_w, ''),
		clock_timestamp(),
		cd_pessoa_fisica_w,
		nm_participante_w,
		nextval('cirurgia_participante_seq'),
		'A');

End Loop;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
