CREATE OR REPLACE PROCEDURE duplicar_pergunta_modelo ( nr_seq_pergunta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_perg_w		bigint;
cd_item_w			varchar(5);
ds_item_w 			varchar(200);
dt_atualizacao_w 		timestamp;
nm_usuario_w			varchar(255);
dt_atualizacao_nrec_w 		timestamp;
nm_usuario_nrec_w 		varchar(255);
nr_seq_apres_w			bigint;

C01 CURSOR FOR
	SELECT	cd_item,
		ds_item,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_apres
	from	pergunta_result
	where	nr_seq_pergunta = nr_seq_pergunta_p;


BEGIN

select	nextval('pergunta_seq')
into STRICT	nr_sequencia_perg_w
;

insert into pergunta(nr_sequencia, ds_pergunta, ie_componente,
	ie_situacao, nr_seq_grupo, ie_tipo_atributo,
	dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
	nm_usuario_nrec, nr_seq_superior)
(SELECT	nr_sequencia_perg_w, ds_pergunta, ie_componente,
	ie_situacao, nr_seq_grupo, ie_tipo_atributo,
	clock_timestamp(), nm_usuario_p,clock_timestamp(),
	nm_usuario_p, nr_seq_superior
from	pergunta
where	nr_sequencia = nr_seq_pergunta_p);

open C01;
loop
fetch C01 into
	cd_item_w,
	ds_item_w,
	dt_atualizacao_w,
	nm_usuario_w,
	dt_atualizacao_nrec_w,
	nm_usuario_nrec_w,
	nr_seq_apres_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into pergunta_result(nr_sequencia, nr_seq_pergunta, cd_item,
		ds_item, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_apres)
	values (nextval('pergunta_result_seq'), nr_sequencia_perg_w, cd_item_w,
		ds_item_w, clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, nr_seq_apres_w);
	end;
end loop;
close C01;

commit;

end;
$body$
