CREATE OR REPLACE PROCEDURE duplicar_regra_convenio_plano (nr_seq_regra_origem_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
nr_seq_w	bigint;
		
C01 CURSOR FOR
	SELECT	a.*
	from	REGRA_CONV_PLANO_ITEM a
	where	a.nr_seq_regra = nr_seq_regra_origem_p;	
	
	
c01_w	C01%rowtype;
	

BEGIN

select 	nextval('regra_convenio_plano_seq')
into STRICT	nr_sequencia_w
;

insert into REGRA_CONVENIO_PLANO(nr_sequencia,
	cd_convenio,
	cd_plano,
	ie_regra,
	dt_atualizacao,
	nm_usuario,
	ie_tipo_atendimento,
	cd_setor_atendimento,
	cd_area_procedimento,
	cd_especialidade_proc,
	cd_grupo_proc,
	cd_procedimento,
	ie_origem_proced,
	cd_tipo_acomodacao,
	ie_valor,
	qt_ponto_min,
	qt_ponto_max,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	nr_seq_grupo,
	nr_seq_subgrupo, 
	nr_seq_forma_org,
	ie_prioridade,
	cd_categoria,
	ds_mascara_carteira,
	cd_estabelecimento,
	cd_doenca,
	ie_situacao,
	ie_nova_autorizacao,
	ie_regra_valor,
	ie_autor_kit,
    cd_empresa
	)
SELECT	nr_sequencia_w,
	cd_convenio,
	cd_plano,
	ie_regra,
	clock_timestamp(),
	nm_usuario_p,
	ie_tipo_atendimento,
	cd_setor_atendimento,
	null,
	null,
	null,
	cd_procedimento_p,
	ie_origem_proced_p,
	cd_tipo_acomodacao,
	ie_valor,
	qt_ponto_min,
	qt_ponto_max,
	dt_inicio_vigencia,
	dt_fim_vigencia, 
	nr_seq_grupo,
	nr_seq_subgrupo, 
	nr_seq_forma_org,
	ie_prioridade,
	cd_categoria,
	ds_mascara_carteira,
	cd_estabelecimento,
	cd_doenca,
	coalesce(ie_situacao,'A'),
	coalesce(ie_nova_autorizacao,'N'),
	coalesce(ie_regra_valor, 'FF'),
	coalesce(IE_AUTOR_KIT,'N'),
    cd_empresa
from	regra_convenio_plano
where	nr_sequencia	= nr_seq_regra_origem_p;

open C01;
loop
fetch C01 into	
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	select 	nextval('regra_conv_plano_item_seq')
	into STRICT	nr_seq_w
	;
	
	insert into REGRA_CONV_PLANO_ITEM(
			NR_SEQUENCIA,
			DT_ATUALIZACAO,
			NM_USUARIO,
			DT_ATUALIZACAO_NREC,
			NM_USUARIO_NREC,
			NR_SEQ_REGRA,
			CD_PROCEDIMENTO,
			IE_ORIGEM_PROCED,
			CD_MATERIAL,
			QT_SOLICITADA,
			IE_SITUACAO,
			NR_SEQ_PROC_INTERNO)
		values (nr_seq_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_w,
			c01_w.CD_PROCEDIMENTO,
			c01_w.IE_ORIGEM_PROCED,
			c01_w.CD_MATERIAL,
			c01_w.QT_SOLICITADA,
			c01_w.IE_SITUACAO,
			c01_w.NR_SEQ_PROC_INTERNO);	
	
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
