SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplicar_regra_orientacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;
nr_seq_doc_atend_w	bigint;


BEGIN

select	nextval('convenio_doc_atend_seq')
into STRICT	nr_seq_doc_atend_w
;

insert into convenio_doc_atend(nr_sequencia,
	cd_convenio,
	ie_documento,
	dt_atualizacao,
	nm_usuario,
	cd_estabelecimento,
	cd_plano,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_titulo,
	ds_arquivo,
	cd_categoria,
	ds_documento)
SELECT	nr_seq_doc_atend_w,
	cd_convenio,
	ie_documento,
	clock_timestamp(),
	nm_usuario_p,
	cd_estabelecimento,
	cd_plano,
	clock_timestamp(),
	nm_usuario_p,
	ds_titulo,
	ds_arquivo,
	cd_categoria,
	' '
from	convenio_doc_atend
where	nr_sequencia = nr_sequencia_p;

CALL copia_campo_long('CONVENIO_DOC_ATEND','DS_DOCUMENTO','WHERE NR_SEQUENCIA = :NR_SEQUENCIA',
								 'NR_SEQUENCIA=' || nr_sequencia_p,'NR_SEQUENCIA=' || nr_seq_doc_atend_w);

select	count(*)
into STRICT	qt_existe_w
from	convenio_doc_atend_regra
where	nr_seq_doc_atend = nr_sequencia_p;

if (qt_existe_w > 0) then

	insert into convenio_doc_atend_regra(nr_sequencia,
		cd_estabelecimento,
		nr_seq_doc_atend,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_perfil,
		ie_tipo_atendimento,
		ie_situacao,
		nr_seq_classificacao,
		cd_funcao,
		dt_vigencia_inicial,
		dt_vigencia_final,
		cd_empresa,
		hr_inicial,
		hr_final)
	SELECT	nextval('convenio_doc_atend_regra_seq'),
		cd_estabelecimento,
		nr_seq_doc_atend_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_perfil,
		ie_tipo_atendimento,
		ie_situacao,
		nr_seq_classificacao,
		cd_funcao,
		dt_vigencia_inicial,
		dt_vigencia_final,
		cd_empresa,
		hr_inicial,
		hr_final
	from	convenio_doc_atend_regra
	where	nr_seq_doc_atend = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
