CREATE OR REPLACE PROCEDURE duplicar_sae_dias ( nr_seq_prescr_p bigint, cd_prescritor_p text, nr_atendimento_p bigint, qt_dias_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_intervencao_w	varchar(1);
nr_seq_modelo_w		bigint;
nr_seq_prescr_ret_w	bigint;
dt_sae_w		timestamp;
				
BEGIN 
ie_intervencao_w := obter_param_usuario(281, 216, obter_perfil_ativo, nm_usuario_p, coalesce(obter_estab_atend(nr_atendimento_p),0), ie_intervencao_w);
 
if (coalesce(qt_dias_p,0) > 0) then 
	 
	select	max(nr_seq_modelo) 
	into STRICT	nr_seq_modelo_w 
	from	pe_prescricao 
	where	nr_sequencia = nr_seq_prescr_p;
	 
	nr_seq_prescr_ret_w := duplicar_prescricao_enfemagem(nr_seq_prescr_p, cd_prescritor_p, nr_atendimento_p, clock_timestamp(), ie_intervencao_w, 'N', nm_usuario_p, nr_seq_modelo_w, nr_seq_prescr_ret_w, 'SAE');
	 
	CALL liberar_prescricao_enfemagem(nr_seq_prescr_ret_w, nm_usuario_p);
	 
	if (qt_dias_p > 1) then 
		for i in 1..qt_dias_p-1 loop 
			begin 
			select	max(dt_prescricao)+1 
			into STRICT	dt_sae_w 
			from	pe_prescricao 
			where	nr_sequencia = nr_seq_prescr_ret_w;
			 
			nr_seq_prescr_ret_w := duplicar_prescricao_enfemagem(nr_seq_prescr_p, cd_prescritor_p, nr_atendimento_p, dt_sae_w, ie_intervencao_w, 'N', nm_usuario_p, nr_seq_modelo_w, nr_seq_prescr_ret_w, 'SAE');
			 
			CALL liberar_prescricao_enfemagem(nr_seq_prescr_ret_w, nm_usuario_p);
			end;
		end loop;
	end if;
end if;
				 
end;
$body$
