SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplicar_sae_sdc ( nr_atendimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE


nr_sequencia_w				pe_prescricao.nr_sequencia%type;
cd_prescritor_w				pe_prescricao.cd_prescritor%type;
ie_tipo_pessoa_w  			varchar(10);
nr_prescricao_nova_w 		pe_prescricao.nr_sequencia%type;
cd_perfil_w					pe_prescricao.cd_perfil_ativo%type	:= obter_perfil_ativo;
ie_situacao_w				pe_prescricao.ie_situacao%type;
dt_primeiro_horario_w		pe_prescricao.dt_primeiro_horario%type;
ie_agora_w					pe_prescricao.ie_agora%type;
nr_sequencia_sae_w    		pe_prescricao.nr_sequencia%type;


BEGIN

select
	max(a.nr_sequencia),
	max(a.cd_prescritor),
	max(a.ie_situacao),
	max(a.dt_primeiro_horario),
	max(a.ie_agora)
into STRICT
	nr_sequencia_w,
	cd_prescritor_w,
	ie_situacao_w,
	dt_primeiro_horario_w,
	ie_agora_w
from (	SELECT 	b.nr_sequencia, 
				b.cd_prescritor, 
				b.ie_situacao, 
				b.dt_primeiro_horario, 
				b.ie_agora, 
				b.nr_prescricao 

		from 	pe_prescricao b,
				atendimento_paciente ate
				
		where 	ate.nr_atendimento = b.nr_atendimento
		and		coalesce(ate.dt_alta::text, '') = ''
		and		coalesce(b.ie_situacao,'A') <> 'I' 
		and 	b.ie_tipo = 'SAE' 
		and 	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
		and 	coalesce(b.dt_inativacao::text, '') = ''
		and 	b.nr_atendimento = nr_atendimento_p
		and		coalesce(dt_suspensao::text, '') = ''
		order by b.dt_liberacao desc) a LIMIT 1;


ie_tipo_pessoa_w := obter_se_usuario_medico(nm_usuario_p);


if ( coalesce(nr_sequencia_w,0) > 0  and coalesce(cd_prescritor_w,0) > 0 ) then
	
	
	nr_sequencia_sae_w := duplicar_sae(nr_sequencia_w, cd_prescritor_w, nr_atendimento_p, nm_usuario_p, nr_sequencia_sae_w, 'S');
		
	
	select	coalesce(max(a.nr_prescricao),0)
	into STRICT	nr_prescricao_nova_w
	from	pe_prescricao a
	where	nr_sequencia	= nr_sequencia_sae_w;
	
	if (nr_prescricao_nova_w > 0) then
	
		CALL liberar_prescricao_enfemagem(nr_sequencia_sae_w, nm_usuario_p);	commit;

	end if;
	

end if;
		

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
