SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplicar_texto_padrao_pepa (nr_seq_texto_padrao_p bigint, ie_commit_p text default 'N') AS $body$
DECLARE


ehr_texto_padrao_w			ehr_texto_padrao%rowtype;
ehr_texto_padrao_lib_w		ehr_texto_padrao_lib%rowtype;

c01 CURSOR FOR
	SELECT	*
	from	ehr_texto_padrao_lib
	where	nr_seq_texto = nr_seq_texto_padrao_p;

BEGIN

	select	*
	into STRICT	ehr_texto_padrao_w
	from	ehr_texto_padrao
	where	nr_sequencia = nr_seq_texto_padrao_p;

	select	nextval('ehr_texto_padrao_seq'),
			ehr_texto_padrao_w.ds_titulo || '_' || obter_desc_expressao(347811)
	into STRICT	ehr_texto_padrao_w.nr_sequencia,
			ehr_texto_padrao_w.ds_titulo
	;

	ehr_texto_padrao_w.dt_atualizacao := clock_timestamp();
	ehr_texto_padrao_w.nm_usuario := wheb_usuario_pck.get_nm_usuario;
	ehr_texto_padrao_w.dt_atualizacao_nrec := clock_timestamp();
	ehr_texto_padrao_w.nm_usuario_nrec := wheb_usuario_pck.get_nm_usuario;

	insert into ehr_texto_padrao values (ehr_texto_padrao_w.*);
	commit;

	for c01_w in c01 loop
		select	*
		into STRICT	ehr_texto_padrao_lib_w
		from	ehr_texto_padrao_lib
		where	nr_sequencia = c01_w.nr_sequencia;

		select	nextval('ehr_texto_padrao_lib_seq')
		into STRICT	ehr_texto_padrao_lib_w.nr_sequencia
		;

		ehr_texto_padrao_lib_w.nr_seq_texto := ehr_texto_padrao_w.nr_sequencia;

		ehr_texto_padrao_lib_w.dt_atualizacao := clock_timestamp();
		ehr_texto_padrao_lib_w.nm_usuario := wheb_usuario_pck.get_nm_usuario;
		ehr_texto_padrao_lib_w.dt_atualizacao_nrec := clock_timestamp();
		ehr_texto_padrao_lib_w.nm_usuario_nrec := wheb_usuario_pck.get_nm_usuario;

		insert into ehr_texto_padrao_lib values (ehr_texto_padrao_lib_w.*);
		commit;
	end loop;

	if (coalesce(ie_commit_p,'N') = 'S') and (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
