SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplicar_texto_padrao (nr_seq_texto_padrao_p bigint, ie_commit_p text default 'N') AS $body$
DECLARE


med_texto_padrao_w			med_texto_padrao%rowtype;
med_texto_padrao_imagem_w	med_texto_padrao_imagem%rowtype;

c01 CURSOR FOR
	SELECT	*
	from	med_texto_padrao_imagem
	where	nr_seq_texto = nr_seq_texto_padrao_p;

BEGIN

	select	*
	into STRICT	med_texto_padrao_w
	from	med_texto_padrao
	where	nr_sequencia = nr_seq_texto_padrao_p;

	select	nextval('med_texto_padrao_seq')
	into STRICT	med_texto_padrao_w.nr_sequencia
	;

	select	med_texto_padrao_w.ds_titulo || '_' || obter_desc_expressao(347811)
	into STRICT	med_texto_padrao_w.ds_titulo
	;

	med_texto_padrao_w.dt_atualizacao := clock_timestamp();
	med_texto_padrao_w.nm_usuario := wheb_usuario_pck.get_nm_usuario;
	med_texto_padrao_w.dt_atualizacao_nrec := clock_timestamp();
	med_texto_padrao_w.nm_usuario_nrec := wheb_usuario_pck.get_nm_usuario;

	insert into med_texto_padrao values (med_texto_padrao_w.*);
	commit;

	for c01_w in c01 loop
		select	*
		into STRICT	med_texto_padrao_imagem_w
		from	med_texto_padrao_imagem
		where	nr_sequencia = c01_w.nr_sequencia;

		med_texto_padrao_imagem_w.nr_seq_texto := med_texto_padrao_w.nr_sequencia;

		select	nextval('med_texto_padrao_imagem_seq')
		into STRICT	med_texto_padrao_imagem_w.nr_sequencia
		;

		med_texto_padrao_imagem_w.dt_atualizacao := clock_timestamp();
		med_texto_padrao_imagem_w.nm_usuario := wheb_usuario_pck.get_nm_usuario;
		med_texto_padrao_imagem_w.dt_atualizacao_nrec := clock_timestamp();
		med_texto_padrao_imagem_w.nm_usuario_nrec := wheb_usuario_pck.get_nm_usuario;

		insert into med_texto_padrao_imagem values (med_texto_padrao_imagem_w.*);
		commit;
	end loop;

	if (coalesce(ie_commit_p,'N') = 'S') and (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
