SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplicar_vendedores ( cd_pessoa_fisica_p text, cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_situacao_w		varchar(1);
nr_telefone_ww		varchar(15);
ds_email_ww		varchar(80);
nr_fax_ww		varchar(15);
qt_registros_w		bigint;


BEGIN

select	coalesce(ie_situacao,'A') ie_situacao,
	coalesce(nr_telefone,'') nr_telefone,
	coalesce(ds_email,'') ds_email,
	coalesce(nr_fax,'') nr_fax
into STRICT	ie_situacao_w,
	nr_telefone_ww,
	ds_email_ww,
	nr_fax_ww
from	comprador
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	cd_estabelecimento = cd_estab_origem_p;

select	count(*)
into STRICT	qt_registros_w
from	comprador
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	cd_estabelecimento = cd_estab_destino_p;

if (qt_registros_w > 0) then

	update	comprador
	set	ds_email = ds_email_ww,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario = nm_usuario_p,
		nr_fax = nr_fax_ww,
		nr_telefone = nr_telefone_ww,
		ie_situacao = ie_situacao_w
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	cd_estabelecimento = cd_estab_destino_p;

else
	insert into comprador(
		cd_estabelecimento,
		cd_pessoa_fisica,
		ds_email,
		dt_atualizacao,
		dt_atualizacao_nrec,
		ie_situacao,
		nm_guerra,
		nm_usuario,
		nm_usuario_nrec,
		nr_fax,
		nr_telefone)
	SELECT	cd_estab_destino_p,
		cd_pessoa_fisica_p,
		ds_email,
		clock_timestamp(),
		clock_timestamp(),
		ie_situacao,
		nm_guerra,
		nm_usuario_p,
		nm_usuario_p,
		nr_fax,
		nr_telefone
	from	comprador
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	cd_estabelecimento = cd_estab_origem_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
