CREATE OR REPLACE PROCEDURE duplicar_visao ( nr_sequencia_p bigint, nm_usuario_p text, nr_seq_criado_p INOUT bigint) AS $body$
DECLARE

ie_componente_w	varchar(10);
nm_tabela_w		varchar(255);
nm_atributo_w	varchar(255);
nr_seq_visao_w	bigint;

C01 CURSOR FOR
	SELECT	*
	from	tabela_visao
	where	nr_sequencia = nr_sequencia_p;


c01_w c01%rowtype;


BEGIN


open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin


	select	nextval('tabela_visao_seq')
	into STRICT	nr_seq_visao_w
	;




	insert into TABELA_VISAO(
				 NR_SEQUENCIA,
				 NM_TABELA,
				 DT_ATUALIZACAO,
				 NM_USUARIO,
				 NM_VISAO,
				 DS_UTILIZACAO,
				 QT_POS_ESQUERDA,
				 QT_POS_TOPO,
				 QT_INTERVALO_CAMPO,
				 QT_MAX_CAMPO,
				 QT_ALTURA_PANEL,
				 QT_LARGURA_PANEL,
				 DT_ATUALIZACAO_NREC,
				 NM_USUARIO_NREC,
				 DS_TITULO,
				 DS_SQL_ATIVACAO,
				 CD_FUNCAO,
				 NR_SEQ_DIC_OBJ,
				 IE_UTILIZA_CAMPOS_VISAO,
				 DS_SQL_TOTALIZADOR,
				 IE_UTILIZACAO,
				 DS_CLASSE_JAVA_VALIDACAO,
				 NR_SEQ_OBJETO_WCPANEL,
				 DS_SQL_CUSTOMIZADO,
				 DS_TITULO_DETALHE,
				 CD_EXP_TITULO)
			values (
				 nr_seq_visao_w,
				 c01_w.NM_TABELA,
				 clock_timestamp(),
				 nm_usuario_p,
				 --CÃ³pia = 303214
         substr(obter_desc_expressao(303214)||' - '||c01_w.NM_VISAO,1,50),
				 c01_w.DS_UTILIZACAO,
				 c01_w.QT_POS_ESQUERDA,
				 c01_w.QT_POS_TOPO,
				 c01_w.QT_INTERVALO_CAMPO,
				 c01_w.QT_MAX_CAMPO,
				 c01_w.QT_ALTURA_PANEL,
				 c01_w.QT_LARGURA_PANEL,
				 clock_timestamp(),
				 nm_usuario_p,
				 c01_w.DS_TITULO,
				 c01_w.DS_SQL_ATIVACAO,
				 c01_w.CD_FUNCAO,
				 c01_w.NR_SEQ_DIC_OBJ,
				 c01_w.IE_UTILIZA_CAMPOS_VISAO,
				 c01_w.DS_SQL_TOTALIZADOR,
				 'H',
				 c01_w.DS_CLASSE_JAVA_VALIDACAO,
				 c01_w.NR_SEQ_OBJETO_WCPANEL,
				 c01_w.DS_SQL_CUSTOMIZADO,
				 c01_w.DS_TITULO_DETALHE,
				 c01_w.CD_EXP_TITULO);

	insert into TABELA_SISTEMA_ATIVACAO(
				 NR_SEQUENCIA,
				 NM_USUARIO_NREC,
				 DT_ATUALIZACAO_NREC,
				 NM_USUARIO,
				 DT_ATUALIZACAO,
				 NM_TABELA,
				 NR_SEQ_VISAO,
				 DS_SQL)
	SELECT	nextval('tabela_sistema_ativacao_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_tabela,
			nr_seq_visao_w,
			DS_SQL_ATIVACAO
	from	tabela_visao
	where	nr_sequencia	= nr_sequencia_p
	and		(DS_SQL_ATIVACAO IS NOT NULL AND DS_SQL_ATIVACAO::text <> '');


	insert into TABELA_SISTEMA_ATIVACAO(
				 NR_SEQUENCIA,
				 NM_USUARIO_NREC,
				 DT_ATUALIZACAO_NREC,
				 NM_USUARIO,
				 DT_ATUALIZACAO,
				 NM_TABELA,
				 NR_SEQ_VISAO,
				 DS_SQL)
	SELECT	nextval('tabela_sistema_ativacao_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_tabela,
			nr_seq_visao_w,
			ds_sql
	from	TABELA_SISTEMA_ATIVACAO
	where	nr_seq_visao	= nr_sequencia_p;

	CALL COPIAR_VISAO(nr_sequencia_p,nr_seq_visao_w,nm_usuario_p);


	end;
end loop;
close C01;






commit;

end;
$body$
