SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplicate_perfil_calorico ( nr_sequencia_p dieta_perfil_calorico_nut.nr_sequencia%type ) AS $body$
DECLARE


nr_sequencia_novo_w     dieta_perfil_calorico_nut.nr_sequencia%type;
nr_seq_novo_perfil_w    dieta_pc_comp_nutr.nr_sequencia%type;
nm_usuario_dupl_w       varchar(15);

c01 CURSOR FOR
SELECT
    nr_sequencia,
    dt_atualizacao,
    nm_usuario,
    dt_atualizacao_nrec,
    nm_usuario_nrec,
    qt_composicao,
    nr_seq_nutriente,
    cd_unidade_medida,
    nr_seq_perfil_cal,
    nr_seq_apresent
from     dieta_pc_comp_nutr 
where    nr_seq_perfil_cal = nr_sequencia_p
order by nr_seq_apresent;

BEGIN

select nextval('dieta_perfil_calorico_nut_seq') into STRICT nr_sequencia_novo_w;
nm_usuario_dupl_w := wheb_usuario_pck.get_nm_usuario;

insert into dieta_perfil_calorico_nut(
    nr_sequencia,
    dt_atualizacao,
    nm_usuario,
    dt_atualizacao_nrec,
    nm_usuario_nrec,
    cd_dieta,
    ds_perfil_calorico)
SELECT
    nr_sequencia_novo_w,
    clock_timestamp(),
    nm_usuario_dupl_w,
    clock_timestamp(),
    nm_usuario_dupl_w,
    cd_dieta,
    ds_perfil_calorico
from    dieta_perfil_calorico_nut
where   nr_sequencia = nr_sequencia_p;

for item in c01 loop
    select nextval('dieta_pc_comp_nutr_seq') into STRICT nr_seq_novo_perfil_w;

    insert into dieta_pc_comp_nutr(
        nr_sequencia,
        dt_atualizacao,
        nm_usuario,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        qt_composicao,
        nr_seq_nutriente,
        cd_unidade_medida,
        nr_seq_perfil_cal,
        nr_seq_apresent)
    values (
        nr_seq_novo_perfil_w,
        clock_timestamp(),
        nm_usuario_dupl_w,
        clock_timestamp(),
        nm_usuario_dupl_w,
        item.qt_composicao,
        item.nr_seq_nutriente,
        item.cd_unidade_medida,
        nr_sequencia_novo_w,
        item.nr_seq_apresent);

end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
