CREATE OR REPLACE PROCEDURE duplicate_register_houdini ( NR_SEQUENCIA_P INOUT W_APAP_PAC_REGISTRO.NR_SEQUENCIA%TYPE ) AS $body$
DECLARE

    W_APAP_REGISTRO_ROW W_APAP_PAC_REGISTRO%ROWTYPE;
    NM_USUARIO_W USUARIO.NM_USUARIO%TYPE := null;

BEGIN

    SELECT * INTO STRICT W_APAP_REGISTRO_ROW FROM W_APAP_PAC_REGISTRO WHERE NR_SEQUENCIA = NR_SEQUENCIA_P;

    NM_USUARIO_W := wheb_usuario_pck.get_nm_usuario;

    if ((W_APAP_REGISTRO_ROW.NR_SEQ_ORIGEM IS NOT NULL AND W_APAP_REGISTRO_ROW.NR_SEQ_ORIGEM::text <> '') and (coalesce(W_APAP_REGISTRO_ROW.IE_STATUS_HOUDINI::text, '') = '' or W_APAP_REGISTRO_ROW.IE_STATUS_HOUDINI <> 'L')) THEN

        IF (coalesce(W_APAP_REGISTRO_ROW.IE_STATUS_HOUDINI::text, '') = '') THEN
    
            UPDATE W_APAP_PAC_REGISTRO SET
                DT_ATUALIZACAO = clock_timestamp(),
                NM_USUARIO = NM_USUARIO_W,
                IE_STATUS_HOUDINI = 'U'
            WHERE
                NR_SEQUENCIA = NR_SEQUENCIA_P;

                
            SELECT nextval('w_apap_pac_registro_seq') INTO STRICT NR_SEQUENCIA_P;

            W_APAP_REGISTRO_ROW.NR_SEQUENCIA := NR_SEQUENCIA_P;
            W_APAP_REGISTRO_ROW.DT_ATUALIZACAO := clock_timestamp();
            W_APAP_REGISTRO_ROW.NM_USUARIO :=  NM_USUARIO_W;
            W_APAP_REGISTRO_ROW.IE_STATUS_HOUDINI := 'L';
			W_APAP_REGISTRO_ROW.IE_STATUS_REGISTRO := 'P';

            INSERT INTO W_APAP_PAC_REGISTRO VALUES (W_APAP_REGISTRO_ROW.*);

        ELSIF (W_APAP_REGISTRO_ROW.NM_USUARIO <>  NM_USUARIO_W) THEN
            
            SELECT nextval('w_apap_pac_registro_seq') INTO STRICT NR_SEQUENCIA_P;

            W_APAP_REGISTRO_ROW.NR_SEQUENCIA := NR_SEQUENCIA_P;
            W_APAP_REGISTRO_ROW.DT_ATUALIZACAO := clock_timestamp();
            W_APAP_REGISTRO_ROW.NM_USUARIO :=  NM_USUARIO_W;
            W_APAP_REGISTRO_ROW.IE_STATUS_HOUDINI := 'U';
			W_APAP_REGISTRO_ROW.IE_STATUS_REGISTRO := 'P';

            INSERT INTO W_APAP_PAC_REGISTRO VALUES (W_APAP_REGISTRO_ROW.*);

            SELECT nextval('w_apap_pac_registro_seq') INTO STRICT NR_SEQUENCIA_P;

            W_APAP_REGISTRO_ROW.NR_SEQUENCIA := NR_SEQUENCIA_P;
            W_APAP_REGISTRO_ROW.DT_ATUALIZACAO := clock_timestamp();
            W_APAP_REGISTRO_ROW.NM_USUARIO :=  NM_USUARIO_W;
            W_APAP_REGISTRO_ROW.IE_STATUS_HOUDINI := 'L';
			W_APAP_REGISTRO_ROW.IE_STATUS_REGISTRO := 'P';

            INSERT INTO W_APAP_PAC_REGISTRO VALUES (W_APAP_REGISTRO_ROW.*);

        END IF;

        COMMIT;
    END IF;
END;
$body$
