SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplica_regra_repasse_pont ( nr_seq_regra_origem_p bigint, ds_pontuacao_p text, cd_pontuacao_p bigint, dt_inicio_vigencia_p timestamp, dt_fim_vigencia_p timestamp, nm_usuario_p text, nr_seq_regra_gerada_p INOUT bigint, ie_duplicar_p text) AS $body$
DECLARE



nr_seq_regra_gerada_w	bigint;
nr_seq_pont_tem_w		bigint;
nr_seq_adicional_w	bigint;
nr_seq_rep_ponto_w 	bigint;
nr_seq_mat_criterio_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	regra_repasse_pont_item a
where	a.nr_seq_regra_rep_ponto	= nr_seq_rep_ponto_w;

c02 CURSOR FOR
SELECT 	a.nr_sequencia
from     mat_criterio_repasse a
where    coalesce(a.ie_tipo_criterio,0) = 1
and      exists ( 	SELECT 	*
	from 	mat_crit_rep_regra_ponto b
	where 	b.nr_seq_mat_criterio = a.nr_sequencia
	and      b.nr_seq_regra_rep_ponto = nr_seq_rep_ponto_w);


BEGIN

	select 	nr_seq_regra_rep_ponto,
		nr_seq_mat_criterio
	into STRICT 	nr_seq_rep_ponto_w,
		nr_seq_mat_criterio_w
	from 	mat_crit_rep_regra_ponto
	where 	nr_sequencia = nr_seq_regra_origem_p;

	select	nextval('regra_repasse_pontuacao_seq')
	into STRICT	nr_seq_regra_gerada_w
	;

	insert	into regra_repasse_pontuacao(
		nr_sequencia,
		cd_estabelecimento,   
		dt_atualizacao,     
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_regra_pontuacao,
		ie_tipo_pontuacao,
		vl_unitario_ponto,
		nr_seq_trans_financ,  
		pr_desconto_repasse,
		ie_situacao,  
		dt_inicio_vigencia,   
		dt_fim_vigencia)
	SELECT	nr_seq_regra_gerada_w, 
		a.cd_estabelecimento,   
		clock_timestamp(),     
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		coalesce(ds_pontuacao_p,a.ds_regra_pontuacao),
		a.ie_tipo_pontuacao,
		coalesce(cd_pontuacao_p,a.vl_unitario_ponto),
		a.nr_seq_trans_financ,  
		a.pr_desconto_repasse,
		'A',  
		dt_inicio_vigencia_p,   
		dt_fim_vigencia_p
	from	regra_repasse_pontuacao a
	where	a.nr_sequencia	= nr_seq_rep_ponto_w;
	
	open c01;
	loop
	fetch c01 into
		nr_seq_pont_tem_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		insert	into regra_repasse_pont_item(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,  
			nm_usuario_nrec,
			nr_seq_regra_rep_ponto, 
			dt_inicio_vigencia,     
			dt_fim_vigencia,     
			qt_item_periodo,
			ie_tipo_periodo_repasse,
			ie_forma_cons_qtd_item,
			ie_tipo_data_item,
			vl_inicial_faixa,
			vl_final_faixa, 
			qt_ponto_faixa,
			pr_repassar,
			qt_ponto_item)
		SELECT	nextval('regra_repasse_pont_item_seq'),
			clock_timestamp(),     
			nm_usuario_p,
			clock_timestamp(),  
			nm_usuario_p,
			nr_seq_regra_gerada_w, 
			coalesce(dt_inicio_vigencia_p,clock_timestamp()),     
			dt_fim_vigencia_p,     
			b.qt_item_periodo,
			b.ie_tipo_periodo_repasse,
			b.ie_forma_cons_qtd_item,
			b.ie_tipo_data_item,
			b.vl_inicial_faixa,
			b.vl_final_faixa, 
			b.qt_ponto_faixa,
			b.pr_repassar,
			b.qt_ponto_item
		from	regra_repasse_pont_item b
		where 	b.nr_sequencia	= nr_seq_pont_tem_w
		and 	fim_dia(coalesce(b.dt_fim_vigencia,clock_timestamp()))  >= fim_dia(clock_timestamp());

	end loop;
	close c01;
	
	if (coalesce(ie_duplicar_p,'N') = 'N') then	
		insert	into mat_crit_rep_regra_ponto(
			nr_sequencia,
			dt_atualizacao,      
			nm_usuario, 
			dt_atualizacao_nrec,     
			nm_usuario_nrec,
			nr_seq_mat_criterio,
			nr_seq_regra_rep_ponto)
		SELECT	nextval('mat_crit_rep_regra_ponto_seq'), 
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_mat_criterio_w,   
			nr_seq_regra_gerada_w
		from	mat_crit_rep_regra_ponto a
		where	a.nr_sequencia	= nr_seq_regra_origem_p;	
	else
		open c02;
		loop
		fetch c02 into
			nr_seq_mat_criterio_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */	
			insert	into mat_crit_rep_regra_ponto(
				nr_sequencia,
				dt_atualizacao,      
				nm_usuario, 
				dt_atualizacao_nrec,     
				nm_usuario_nrec,
				nr_seq_mat_criterio,
				nr_seq_regra_rep_ponto)
			SELECT	nextval('mat_crit_rep_regra_ponto_seq'), 
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_mat_criterio_w,   
				nr_seq_regra_gerada_w
			from	mat_crit_rep_regra_ponto a
			where	a.nr_sequencia	= nr_seq_regra_origem_p;		
		end loop;
		close c02;
	end if;

nr_seq_regra_gerada_p	:= nr_seq_regra_gerada_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE duplica_regra_repasse_pont ( nr_seq_regra_origem_p bigint, ds_pontuacao_p text, cd_pontuacao_p bigint, dt_inicio_vigencia_p timestamp, dt_fim_vigencia_p timestamp, nm_usuario_p text, nr_seq_regra_gerada_p INOUT bigint, ie_duplicar_p text) FROM PUBLIC;

