SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplic_organograma ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_dep_philips_w	bigint;
nr_seq_grupo_w		bigint;
nr_seq_dep_novo_w	bigint;
nr_seq_gerencia_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	departamento_philips
	where	NR_GRUPO_DEP_PHILIPS = nr_sequencia_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	depto_gerencia_philips
	where	nr_seq_departamento = nr_seq_dep_philips_w;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	nextval('grupo_dep_philips_seq')
	into STRICT	nr_seq_grupo_w
	;

	insert into grupo_dep_philips(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nm_grupo_dep_philips,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	SELECT	nr_seq_grupo_w,
		clock_timestamp(),
		nm_usuario_p,
		nm_grupo_dep_philips,
		clock_timestamp(),
		nm_usuario_p
	from	grupo_dep_philips
	where	nr_sequencia = nr_sequencia_p;


	open C01;
	loop
	fetch C01 into
		nr_seq_dep_philips_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	nextval('departamento_philips_seq')
		into STRICT	nr_seq_dep_novo_w
		;

		insert into departamento_philips(
			nr_sequencia,
			cd_pessoa_fisica,
			ie_situacao,
			dt_atualizacao,
			nm_usuario,
			cd_classificacao,
			nm_departamento,
			cd_cnpj,
			nr_seq_superior,
			ie_apoio,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_departamento,
			nr_grupo_dep_philips)
		SELECT	nr_seq_dep_novo_w,
			cd_pessoa_fisica,
			ie_situacao,
			clock_timestamp(),
			nm_usuario_p,
			cd_classificacao,
			nm_departamento,
			cd_cnpj,
			nr_seq_superior,
			ie_apoio,
			clock_timestamp(),
			nm_usuario_p,
			ds_departamento,
			nr_seq_grupo_w
		from	departamento_philips
		where	nr_sequencia = nr_seq_dep_philips_w;

		open C02;
		loop
		fetch C02 into
			nr_seq_gerencia_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			insert into depto_gerencia_philips(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				nr_seq_gerencia,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_departamento)
			SELECT	nextval('depto_gerencia_philips_seq'),
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_gerencia,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_dep_novo_w
			from	depto_gerencia_philips
			where	nr_sequencia = 	nr_seq_gerencia_w;

			end;
		end loop;
		close C02;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
