SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE dupl_conv_regra_usuario_cat (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_convenio_w			bigint;
cd_categoria_w			varchar(10);
cd_categoria_atual_w		varchar(10);
nr_sequencia_w			bigint;

C01 CURSOR FOR
	SELECT	cd_categoria
	from	categoria_convenio
	where	ie_situacao	= 'A'
	and	cd_convenio	= cd_convenio_w
	and	cd_categoria	<> cd_categoria_w;



BEGIN

select	max(cd_convenio),
	max(cd_categoria)
into STRICT	cd_convenio_w,
	cd_categoria_w
from	conv_regra_usuario_categ
where	nr_sequencia		= nr_sequencia_p;

open C01;
loop
fetch C01 into
	cd_categoria_atual_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('conv_regra_usuario_categ_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into conv_regra_usuario_categ(nr_sequencia,
		cd_estabelecimento,
		cd_convenio,
		cd_categoria,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_pos_inicial,
		nr_pos_final,
		cd_usuario_padrao,
		ie_tipo_atendimento,
		ie_regra_bloqueio,
		ds_observacao)
	(SELECT	nr_sequencia_w,
		cd_estabelecimento,
		cd_convenio,
		cd_categoria_atual_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_pos_inicial,
		nr_pos_final,
		cd_usuario_padrao,
		ie_tipo_atendimento,
		ie_regra_bloqueio,
		ds_observacao
	from	conv_regra_usuario_categ
	where	nr_sequencia	= nr_sequencia_p);

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
