CREATE OR REPLACE PROCEDURE duv_desfazer_envio (nr_seq_mensagem_p bigint, nr_seq_arquivo_p bigint, nm_usuario_p text) AS $body$
BEGIN

update duv_arquivo
  set ds_arquivo  = NULL,
  DS_ARQUIVO_AUF  = NULL,
  DS_CONTEUDO  = NULL,
  dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
  where nr_sequencia = nr_seq_arquivo_p;

update DUV_MENSAGEM
set dt_envio  = NULL
where NR_SEQUENCIA = nr_seq_mensagem_p;


commit;

end;
