CREATE OR REPLACE PROCEDURE duv_gerar_segmento_abs ( nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


c01 CURSOR FOR
SELECT	substr(a.ds_razao_social,1,120) nm_remetente,
	substr(a.ds_endereco,1,46) ds_endereco_remetente,
	substr(a.cd_cep,1,6) cd_cep_endereco,
	substr(a.ds_municipio,1,30) ds_cidade,
	substr(b.cd_pais_dale_uv,1,3) cd_pais,
	substr(nr_ddi_telefone||' '||nr_ddd_telefone||' '||nr_telefone,1,30) ds_telefone,
	substr(e.nm_pessoa_fisica,1,81) nm_pessoa_contato,
	null nm_medico_avaliador
FROM atendimento_paciente d, estabelecimento c, pessoa_juridica a
LEFT OUTER JOIN pais b ON (a.nr_seq_pais = b.nr_sequencia)
LEFT OUTER JOIN pessoa_fisica e ON (a.cd_pf_resp_tecnico = e.cd_pessoa_fisica)
WHERE a.cd_cgc		= c.cd_cgc and c.cd_estabelecimento	= d.cd_estabelecimento   and d.nr_seq_episodio	= nr_seq_episodio_p;

c01_w c01%rowtype;


BEGIN
c01_w := null;
open c01;
fetch c01 into c01_w;
close c01;

insert into duv_abs(nr_sequencia
	,dt_atualizacao
	,nm_usuario
	,dt_atualizacao_nrec
	,nm_usuario_nrec
	,nr_seq_mensagem
	,nm_remetente
	,ds_endereco_remetente
	,cd_cep_endereco
	,ds_cidade
	,cd_pais
	,ds_telefone
	,nm_pessoa_contato
	,nm_medico_avaliador)
values (nextval('duv_abs_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_mensagem_p,
	c01_w.nm_remetente,
	c01_w.ds_endereco_remetente,
	c01_w.cd_cep_endereco,
	c01_w.ds_cidade,
	c01_w.cd_pais,
	c01_w.ds_telefone,
	c01_w.nm_pessoa_contato,
	c01_w.NM_MEDICO_AVALIADOR);


end;
$body$
