CREATE OR REPLACE PROCEDURE duv_gerar_segmento_ass (nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


  c01 CURSOR FOR
   SELECT
      CASE WHEN pf.ie_sexo='M' THEN 0 WHEN pf.ie_sexo='F' THEN 1 END  ie_sexo,
      null as nr_faixa_etaria, --obs. na definição esta como qt_idade, mas nao existe este atributo.
      null ie_trauma_inalacao,
      null ie_queimadura_terceiro_grau,
      null ie_percent_corpo_queimado,
      null qt_score_total
    from atendimento_paciente ap,
         pessoa_fisica        pf
    where ap.nr_seq_episodio  = nr_seq_episodio_p
      and ap.cd_pessoa_fisica = pf.cd_pessoa_fisica;

  c01_w c01%rowtype;


BEGIN
  c01_w := null;
  open c01;
  fetch c01 into c01_w;
  close c01;
  insert into duv_ass(nr_sequencia,
                      dt_atualizacao,
                      nm_usuario,
                      dt_atualizacao_nrec,
                      nm_usuario_nrec,
                      nr_seq_mensagem,
                      ie_sexo,
                      nr_faixa_etaria,
                      ie_trauma_inalacao,
                      ie_queimadura_terceiro_grau,
                      ie_percent_corpo_queimado,
                      qt_score_total) values (nextval('duv_ass_seq'),
                                              clock_timestamp(),
                                              nm_usuario_p,
                                              clock_timestamp(),
                                              nm_usuario_p,
                                              nr_seq_mensagem_p,
                                              c01_w.ie_sexo,
                                              c01_w.nr_faixa_etaria,
                                              c01_w.ie_trauma_inalacao,
                                              c01_w.ie_queimadura_terceiro_grau,
                                              c01_w.ie_percent_corpo_queimado,
                                              c01_w.qt_score_total);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
