CREATE OR REPLACE PROCEDURE duv_gerar_segmento_bef (nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE



ds_alcool_drogas_medic_w	duv_bef.ds_alcool_drogas_medic%type;

--Definido os valores padrões, enquanto os campos não existem no Tasy.
c01 CURSOR FOR
SELECT 	nr_seq_episodio_p nr_seq_episodio,
	'0' ie_sangramento,
	'1' ie_alcool_drogas_medic,
	obter_desc_tipo_vicio(a.nr_seq_tipo) ds_alcool_drogas_medic
from 	paciente_habito_vicio a,
	atendimento_paciente b
where	a.cd_pessoa_fisica 	= b.cd_pessoa_fisica
and	b.nr_seq_episodio	= nr_seq_episodio_p
and	a.ie_classificacao	in ('A','D','M') --Alkohol, Drogen, Medikamenteneinfluss
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and	coalesce(a.dt_inativacao::text, '') = '';

c01_w c01%rowtype;


BEGIN

ds_alcool_drogas_medic_w := null;

c01_w := null;
open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	if (coalesce(ds_alcool_drogas_medic_w::text, '') = '') or (length(ds_alcool_drogas_medic_w) < 80) then
		if (coalesce(ds_alcool_drogas_medic_w::text, '') = '') then
			ds_alcool_drogas_medic_w := c01_w.ds_alcool_drogas_medic;
		else
			ds_alcool_drogas_medic_w := ds_alcool_drogas_medic_w ||' - '|| c01_w.ds_alcool_drogas_medic;
		end if;
	end if;
end loop;
close C01;

insert into duv_bef(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_mensagem,
	ie_sangramento,
	ie_alcool_drogas_medic,
	ds_alcool_drogas_medic)
values (nextval('duv_bef_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_mensagem_p,
	c01_w.ie_sangramento,
	c01_w.ie_alcool_drogas_medic,
	ds_alcool_drogas_medic_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
