CREATE OR REPLACE PROCEDURE duv_gerar_segmento_beh (nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


  c01 CURSOR FOR
   SELECT
     null ie_tipo_tratamento,
     null ds_sem_tratamento,
     null ie_tipo_atend_duv,
     null ie_medico_tratamento,
     clock_timestamp()                             as dt_retorno,
     substr(es.nm_fantasia_estab,1,81)   as ds_estab_encaminhado,
     substr((pj.ds_endereco||' '||pj.nr_endereco),1,46) as ds_endereco_enc,
     substr(pj.cd_cep,1,6)               as cd_cep_enc,
     substr(pj.cd_municipio_ibge,1,30)   as ds_cidade_dest,
     pa.cd_pais_dale_uv                  as cd_pais_dest,
     'N'                                 as ie_encaminhado
  from atendimento_paciente ap,
       estabelecimento      es,
       pessoa_juridica      pj,
       pais                 pa
  where ap.nr_seq_episodio    = nr_seq_episodio_p
    and ap.cd_estabelecimento = es.cd_estabelecimento
    and es.cd_cgc             = pj.cd_cgc
    and pj.nr_seq_pais        = pa.nr_sequencia;

  c01_w c01%rowtype;


