  c01_w := null;
  open c01;
  fetch c01 into c01_w;
  close c01;
  insert into duv_beh(nr_sequencia,
                        dt_atualizacao,
                        nm_usuario,
                        dt_atualizacao_nrec,
                        nm_usuario_nrec,
                        nr_seq_mensagem,
                        ie_tipo_tratamento,
                        ds_sem_tratamento,
                        ie_tipo_atend_duv,
                        ie_medico_tratamento,
                        dt_retorno,
                        ds_estab_encaminhado,
                        ds_endereco_enc,
                        cd_cep_enc,
                        ds_cidade_dest,
                        cd_pais_dest,
                        ie_encaminhado) values (nextval('duv_beh_seq'),
                                                clock_timestamp(),
                                                nm_usuario_p,
                                                clock_timestamp(),
                                                nm_usuario_p,
                                                nr_seq_mensagem_p,
                                                c01_w.ie_tipo_tratamento,
                                                c01_w.ds_sem_tratamento,
                                                c01_w.ie_tipo_atend_duv,
                                                c01_w.ie_medico_tratamento,
                                                c01_w.dt_retorno,
                                                c01_w.ds_estab_encaminhado,
                                                c01_w.ds_endereco_enc,
                                                c01_w.cd_cep_enc,
                                                c01_w.ds_cidade_dest,
                                                c01_w.cd_pais_dest,
                                                c01_w.ie_encaminhado);

end;
