CREATE OR REPLACE PROCEDURE duv_gerar_segmento_bek (nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


qt_pulso_w		atendimento_sinal_vital.qt_freq_cardiaca%type;
qt_temperatura_w	atendimento_sinal_vital.qt_temp%type;
qt_pressao_art_w	atendimento_sinal_vital.qt_pa_sistolica%type;
hr_exame_w		timestamp;

c01 CURSOR FOR
SELECT	a.dt_avaliacao,
	a.nr_hora,
	a.ie_resposta_motora,
	a.ie_resposta_verbal,
	a.ie_abertura_ocular,
	a.qt_glasgow
from	atend_escala_indice a,
	atendimento_paciente b
where 	a.nr_atendimento 	= b.nr_atendimento
and	b.nr_seq_episodio 	= nr_seq_episodio_p
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and	coalesce(a.dt_inativacao::text, '') = '';

c01_w c01%rowtype;


BEGIN

c01_w := null;

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	begin
		select 	asv.qt_freq_cardiaca qt_pulso,
			asv.qt_temp  qt_temperatura,
			asv.qt_pa_sistolica qt_pressao_art
		into STRICT	qt_pulso_w,
			qt_temperatura_w,
			qt_pressao_art_w
		from  	atendimento_sinal_vital asv,
			atendimento_paciente     ap,
			episodio_paciente        ep
		where 	ep.nr_sequencia  = ap.nr_seq_episodio
		and 	ap.nr_seq_episodio = nr_seq_episodio_p
		and 	asv.nr_atendimento = ap.nr_atendimento  LIMIT 1;
	exception
	when others then
		qt_pulso_w := null;
		qt_temperatura_w:= null;
		qt_pressao_art_w:= null;
	end;

	begin
		select	to_date((to_char(clock_timestamp(),'dd/mm/yyyy')||' '||c01_w.nr_hora||':00:00'),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	hr_exame_w
		;
	exception
	when others then
		hr_exame_w := null;
	end;

	insert into duv_bek(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_mensagem,
		ie_local_exame,
		dt_exame,
		hr_exame,
		ds_profissao,
		ie_abertura_olho,
		ie_resposta_motora,
		ie_resposta_verbal,
		qt_pontos,
		ie_largura_pupila_direita,
		ie_largura_pupila_esquerda,
		ie_reacao_pupila_direita,
		ie_reacao_pupila_esquerca,
		qt_pressao_arterial_rr,
		qt_pulso,
		qt_temperatura)
	values (nextval('duv_bek_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_mensagem_p,
		null, --ie_local_exame,
		c01_w.dt_avaliacao,
		hr_exame_w,
		null, --c01_w.ds_profissao,
		c01_w.ie_abertura_ocular,
		c01_w.ie_resposta_motora,
		c01_w.ie_resposta_verbal,
		c01_w.qt_glasgow,
		null, --ie_largura_pupila_direita,
		null, --ie_largura_pupila_esquerda,
		null, --ie_reacao_pupila_direita,
		null, --ie_reacao_pupila_esquerca,
		qt_pressao_art_w,
		qt_pulso_w,
		qt_temperatura_w);

end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
