CREATE OR REPLACE PROCEDURE duv_gerar_segmento_dis ( nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE

c01 CURSOR FOR
SELECT	'0' ie_origem_cid,
	a.cd_doenca||CASE WHEN a.ie_lado='D' THEN ' R' WHEN a.ie_lado='E' THEN ' L' WHEN a.ie_lado='A' THEN ' B'  ELSE null END  cd_diagnostico
from 	diagnostico_doenca a,
	atendimento_paciente b
where	a.nr_atendimento	= b.nr_atendimento
and	b.nr_seq_episodio	= nr_seq_episodio_p
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and	coalesce(a.dt_inativacao::text, '') = '';

c01_w 	c01%rowtype;


BEGIN

c01_w := null;
open c01;
fetch c01 into c01_w;
close c01;

insert into duv_dis(nr_sequencia
	,dt_atualizacao
	,nm_usuario
	,dt_atualizacao_nrec
	,nm_usuario_nrec
	,nr_seq_mensagem
	,ie_origem_cid
	,cd_diagnostico)
values (nextval('duv_dis_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_mensagem_p,
	c01_w.ie_origem_cid,
	c01_w.cd_diagnostico);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
