CREATE OR REPLACE PROCEDURE duv_gerar_segmento_ksd ( nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


c01 CURSOR FOR
SELECT	a.nr_seq_episodio,
	substr(c.ds_convenio,1,100) ds_convenio,
	CASE WHEN b.IE_TIPO_CONVENIADO='2' THEN '1'  ELSE '0' END  ie_familia_segurado,
	d.cd_cnes cd_convenio_ext,
	null ds_fundo_assistencia,
	null cd_fundo_assistencia
from	atendimento_paciente a,
	atend_categoria_convenio b,
	convenio c,
	pessoa_juridica d
where	a.nr_atendimento	= b.nr_atendimento
and	b.nr_seq_interno	= obter_atecaco_atendimento(a.nr_atendimento)
and	a.nr_seq_episodio	= nr_seq_episodio_p
and	b.cd_convenio		= c.cd_convenio
and	c.cd_cgc		= d.cd_cgc;

c01_w	c01%rowtype;


BEGIN

c01_w := null;
open c01;
fetch c01 into c01_w;
close c01;

insert into duv_ksd(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_mensagem,
	ds_convenio,
	ie_familia_segurado,
	cd_convenio_ext,
	ds_fundo_assistencia,
	cd_fundo_assistencia)
values (nextval('duv_ksd_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_mensagem_p,
	c01_w.ds_convenio,
	c01_w.ie_familia_segurado,
	c01_w.cd_convenio_ext,
	c01_w.ds_fundo_assistencia,
	c01_w.cd_fundo_assistencia);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
