CREATE OR REPLACE PROCEDURE duv_gerar_segmento_kto ( nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


vl_total_conta_w	double precision;
nr_ik_estab_w		varchar(9);

c01 CURSOR FOR
SELECT	a.nr_interno_conta nr_interno_conta
from	conta_paciente a,
	atendimento_paciente b
where	a.nr_atendimento	= b.nr_atendimento
and	b.nr_seq_episodio	= nr_seq_episodio_p
and	a.ie_status_acerto	= '2';

c01_w c01%rowtype;


BEGIN

vl_total_conta_w	:= 0;

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	vl_total_conta_w 	:= vl_total_conta_w + obter_valor_conta(c01_w.nr_interno_conta,0);
	
end loop;
close C01;

select	substr(a.cd_cnes,1,9)
into STRICT	nr_ik_estab_w
from	pessoa_juridica a,
	estabelecimento b,
	atendimento_paciente c
where	a.cd_cgc		= b.cd_cgc
and	b.cd_estabelecimento	= c.cd_estabelecimento
and	c.nr_seq_episodio	= nr_seq_episodio_p  LIMIT 1;

insert into duv_kto(nr_sequencia
	,dt_atualizacao
	,nm_usuario
	,dt_atualizacao_nrec
	,nm_usuario_nrec
	,nr_seq_mensagem
	,VL_TOTAL_CONTA
	,NR_CONTA
	,CD_BENEFICIARIO)
values (nextval('duv_kto_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_mensagem_p,
	vl_total_conta_w,
	c01_w.nr_interno_conta,
	nr_ik_estab_w);


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
