CREATE OR REPLACE PROCEDURE duv_gerar_segmento_sri ( nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


vl_procedimento_w	double precision;
vl_material_w		double precision;
vl_outros_w		double precision;
vl_total_conta_w	double precision;

c01 CURSOR FOR
SELECT	a.nr_interno_conta nr_interno_conta
from	conta_paciente a,
	atendimento_paciente b
where	a.nr_atendimento	= b.nr_atendimento
and	b.nr_seq_episodio	= nr_seq_episodio_p
and	a.ie_status_acerto	= '2';

c01_w c01%rowtype;


BEGIN

vl_procedimento_w	:= 0;
vl_material_w		:= 0;
vl_outros_w		:= 0;
vl_total_conta_w	:= 0;

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	vl_procedimento_w 	:= vl_procedimento_w + obter_valor_conta(c01_w.nr_interno_conta,1);
	vl_material_w 		:= vl_material_w + obter_valor_conta(c01_w.nr_interno_conta,2);
	vl_outros_w 		:= vl_outros_w + (obter_valor_conta(c01_w.nr_interno_conta,4) + obter_valor_conta(c01_w.nr_interno_conta,7));
	vl_total_conta_w 	:= vl_total_conta_w + obter_valor_conta(c01_w.nr_interno_conta,0);

end loop;
close C01;

insert into duv_sri(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_mensagem,
	ie_tipo_tratamento,
	ie_tipo_conta,
	vl_total,
	vl_outros,
	vl_total_conta,
	vl_materiais)
values (nextval('duv_sri_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_mensagem_p,
	null,
	null,
	vl_procedimento_w,
	vl_outros_w,
	vl_total_conta_w,
	vl_material_w);


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
