CREATE OR REPLACE PROCEDURE duv_gerar_segmento_swh (nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


  c01 CURSOR FOR
   SELECT
     'Tasy' as nm_programa,
     substr(at.cd_versao_atual,1,80)   as ds_versao,
     'Philips Clinical Informatics'    as nm_fabricante,
     'DS_PRUEFSCHLUESSEL' as ds_chave_verif
   from aplicacao_tasy at
   where at.cd_aplicacao_tasy = 'Tasy';

  c01_w c01%rowtype;

  c02 CURSOR FOR
    SELECT substr(b.CD_VERSAO,1,80) as ds_versao_dale_uv
    from duv_versao   b,
         duv_mensagem a
    where a.nr_seq_versao = b.nr_sequencia
      and a.nr_sequencia  = nr_seq_mensagem_p;

  c02_w c02%rowtype;


BEGIN
  open c01;
  fetch c01 into c01_w;
  close c01;

  open c02;
  fetch c02 into c02_w;
  close c02;

  insert into duv_swh(nr_sequencia,
                      dt_atualizacao,
                      nm_usuario,
                      dt_atualizacao_nrec,
                      nm_usuario_nrec,
                      nr_seq_mensagem,
                      nm_fabricante,
                      nm_programa,
                      ds_versao,
                      ds_versao_dale_uv,
                      ds_chave_verif) values (nextval('duv_swh_seq'),
                                              clock_timestamp(),
                                              nm_usuario_p,
                                              clock_timestamp(),
                                              nm_usuario_p,
                                              nr_seq_mensagem_p,
                                              c01_w.nm_fabricante,
                                              c01_w.nm_programa,
                                              c01_w.ds_versao,
                                              c02_w.ds_versao_dale_uv,
                                              c01_w.ds_chave_verif);

end;
$body$
