CREATE OR REPLACE PROCEDURE duv_gerar_segmento_tab (nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


  c01 CURSOR FOR
     SELECT
       substr(dd.ds_diagnostico,1,3000) as ds_diagnostico,
       ddia.ds_diagnostico as ds_diag_outro
     FROM atendimento_paciente ap, diagnostico_doenca dd
LEFT OUTER JOIN diag_doenca_inf_adic ddia ON (dd.nr_seq_interno = ddia.nr_seq_diag_doenca)
WHERE ap.nr_seq_episodio = nr_seq_episodio_p and ap.nr_atendimento  = dd.nr_atendimento;

  c01_w c01%rowtype;


BEGIN
  c01_w := null;
  open c01;
  fetch c01 into c01_w;
  close c01;

  if coalesce(c01_w.ds_diag_outro::text, '') = '' then
    c01_w.ds_diag_outro := c01_w.ds_diagnostico;
  end if;

  insert into duv_tab(nr_sequencia,
                      dt_atualizacao,
                      nm_usuario,
                      dt_atualizacao_nrec,
                      nm_usuario_nrec,
                      nr_seq_mensagem,
                      ds_diag_outro) values (nextval('duv_tab_seq'),
                                             clock_timestamp(),
                                             nm_usuario_p,
                                             clock_timestamp(),
                                             nm_usuario_p,
                                             nr_seq_mensagem_p,
                                             c01_w.ds_diag_outro);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
