CREATE OR REPLACE PROCEDURE duv_gerar_segmento_tre (nr_seq_mensagem_p duv_mensagem.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_episodio_p episodio_paciente.nr_sequencia%type) AS $body$
DECLARE


  c01 CURSOR FOR
   SELECT
     substr(dd.DS_DIAGNOSTICO,1,3000) DS_DIAG_OUTRO
   from DIAGNOSTICO_DOENCA dd,
        atendimento_paciente ap
   where ap.nr_seq_episodio = nr_seq_episodio_p
     and ap.nr_atendimento  = dd.nr_atendimento;

  c01_w c01%rowtype;


BEGIN
  c01_w := null;
  open c01;
  fetch c01 into c01_w;
  close c01;

  insert into DUV_tre(NR_SEQUENCIA,
                      DT_ATUALIZACAO,
                      NM_USUARIO,
                      DT_ATUALIZACAO_NREC,
                      NM_USUARIO_NREC,
                      NR_SEQ_MENSAGEM,
                      DS_DIAG_OUTRO) values (nextval('duv_tre_seq'),
                                             clock_timestamp(),
                                             nm_usuario_p,
                                             clock_timestamp(),
                                             nm_usuario_p,
                                             nr_seq_mensagem_p,
                                             c01_w.DS_DIAG_OUTRO);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
