SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duv_importar_consistencia_arq ((nr_seq_mensagem_p bigint, ie_tipo_p text, nm_usuario_p text, ds_xml_p text, ds_retorno_p out text, ds_pruefschluessel_p out text) is /*
ie_tipo_p
C	Consistência do arquivo
E	Consistência realizada no momento do envio do arquivo
*/
 xml_w xml) AS $body$
DECLARE

	c01 CURSOR FOR
	SELECT 	nr_sequencia,
		ie_tipo_segmento
	from 	duv_mensagem_segmento
	where 	nr_seq_mensagem =  nr_seq_mensagem_p;

	c01_w	c01%rowtype;
	nr_retorno_w	bigint;

	
BEGIN
	nr_retorno_w	:= null;

	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		if (coalesce(nr_retorno_w::text, '') = '') then
			if ( position(upper(c01_w.ie_tipo_segmento)  in upper(ds_erro_p)) > 0) then
				nr_retorno_w := c01_w.nr_sequencia;
			end if;
		end if;

	end loop;
	close C01;

	return;
	end;

begin

delete from duv_msg_inconsistencia where nr_seq_mensagem = nr_seq_mensagem_p;

ds_retorno_w 	:= null;
qt_erro_w	:= 0;
qt_alert_w	:= 0;

if (octet_length(ds_xml_p) > 0) then

	xml_w	:= xmlparse(DOCUMENT, convert_from(, 'utf-8'));

	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		fehler_w	:= c01_w.fehler;

		open C02;
		loop
		fetch C02 into
			c02_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */

			nr_seq_segmento_w := null;
			if (c02_w.ds_tag IS NOT NULL AND c02_w.ds_tag::text <> '') then

				ie_segmento_w := substr(c02_w.ds_tag,1,3);

				select	max(nr_sequencia)
				into STRICT	nr_seq_segmento_w
				from	duv_mensagem_segmento
				where	nr_seq_mensagem  = nr_seq_mensagem_p
				and	ie_tipo_segmento = ie_segmento_w;

			else
				nr_seq_segmento_w	:= obter_segmento_erro(c02_w.ds_erro);
			end if;


			select	nextval('duv_msg_inconsistencia_seq')
			into STRICT	duv_msg_inconsistencia_w.nr_sequencia
			;
			duv_msg_inconsistencia_w.dt_atualizacao		:= clock_timestamp();
			duv_msg_inconsistencia_w.nm_usuario		:= nm_usuario_p;
			duv_msg_inconsistencia_w.dt_atualizacao_nrec	:= clock_timestamp();
			duv_msg_inconsistencia_w.nm_usuario_nrec	:= nm_usuario_p;
			duv_msg_inconsistencia_w.nr_seq_mensagem	:= nr_seq_mensagem_p;
			duv_msg_inconsistencia_w.nr_seq_msg_segmento	:= nr_seq_segmento_w;
			duv_msg_inconsistencia_w.ds_identificador	:= c02_w.ds_identificador;
			duv_msg_inconsistencia_w.ds_tag			:= c02_w.ds_tag;
			duv_msg_inconsistencia_w.ds_campo		:= c02_w.ds_campo;
			duv_msg_inconsistencia_w.ie_gravidade		:= c02_w.ie_gravidade;
			duv_msg_inconsistencia_w.ie_nivel		:= c02_w.ie_nivel;
			duv_msg_inconsistencia_w.cd_erro		:= c02_w.cd_erro;
			duv_msg_inconsistencia_w.ds_erro		:= c02_w.ds_erro;
			duv_msg_inconsistencia_w.nr_seq_duv_segmento	:= null;
			duv_msg_inconsistencia_w.ie_sistema_validacao	:= 'DGUV';

			insert into duv_msg_inconsistencia values (duv_msg_inconsistencia_w.*);

			if (c02_w.ie_nivel in ('1','2')) then
				qt_erro_w := qt_erro_w + 1;
			elsif (c02_w.ie_nivel = '3') then
				qt_alert_w := qt_alert_w + 1;
			end if;

		end loop;
		close C02;

		update	duv_mensagem
		set	dt_consistencia	= clock_timestamp()
		where	nr_sequencia	= nr_seq_mensagem_p;

		update	duv_swh
		set	ds_chave_verif 	= c01_w.ds_pruefschluessel
		where	nr_seq_mensagem = nr_seq_mensagem_p;

		ds_pruefschluessel_p	:= c01_w.ds_pruefschluessel;

	end loop;
	close C01;


	if (ie_tipo_p = 'C') then
		if (qt_erro_w > 0) or (qt_alert_w > 0) then
			--Processo de validação finalizado!
			--#@qt_erros#@ erros encontrados.
			--#@qt_alerts#@ alertas.
			ds_retorno_w := wheb_mensagem_pck.get_texto(1027900,'qt_erros='||qt_erro_w||';qt_alerts='||qt_alert_w||';');
		else
			--Processo de validação finalizado!
			--O arquivo está válido.
			ds_retorno_w := wheb_mensagem_pck.get_texto(1027901);
		end if;
	elsif (ie_tipo_p = 'E') then
		if (qt_erro_w > 0) or (qt_alert_w > 0) then
			--O arquivo foi gerado, porém possui erros.
			--Verifique as inconsistências do arquivo na aba 'Inconsistências'.
			ds_retorno_w := wheb_mensagem_pck.get_texto(1027902);
		else
			--Processo finalizado!
			--O arquivo foi gerado com sucesso.
			ds_retorno_w := wheb_mensagem_pck.get_texto(1027903);
		end if;
	end if;
end if;

ds_retorno_p	:= ds_retorno_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE duv_importar_consistencia_arq ((nr_seq_mensagem_p bigint, ie_tipo_p text, nm_usuario_p text, ds_xml_p text, ds_retorno_p out text, ds_pruefschluessel_p out text) is  xml_w xml) FROM PUBLIC;

