SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eclipse_batch_check ( nr_seq_protocolo_p conta_paciente.nr_seq_protocolo%type, nm_usuario_p conta_paciente.nm_usuario%type, ie_validate_P text) AS $body$
DECLARE


ie_eclipse_type_w		varchar(10);
nr_interno_conta_w		conta_paciente.nr_interno_conta%type;
qt_inconsistencies_w	integer;
cd_transaction_w  		varchar(25);


c01 CURSOR FOR
SELECT		a.nr_interno_conta,
			a.cd_estabelecimento
from		conta_paciente a
where		a.nr_seq_protocolo	= nr_seq_protocolo_p
order by	a.nr_atendimento;
c01_w		c01%rowtype;



BEGIN

	open	c01;
	loop
	fetch	c01 into c01_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	nr_interno_conta_w := c01_w.nr_interno_conta;

	if (nr_interno_conta_w IS NOT NULL AND nr_interno_conta_w::text <> '') then

		ie_eclipse_type_w := get_eclipse_claim_type(nr_interno_conta_w);

		if (ie_eclipse_type_w ='IHC') then
			CALL get_ihc_claim(nr_interno_conta_w, c01_w.cd_estabelecimento, nm_usuario_p, 'N');
		elsif (ie_eclipse_type_w = 'DVA') then
			cd_transaction_w := dva_claim_request(nm_usuario_p, c01_w.cd_estabelecimento, null, nr_interno_conta_w, nr_interno_conta_w, 'N', 'T', cd_transaction_w);
		elsif (ie_eclipse_type_w = 'DBS') then
			cd_transaction_w := get_dbs_claim(nr_interno_conta_w, c01_w.cd_estabelecimento, nm_usuario_p, 'N', 'Y', null, cd_transaction_w);
		elsif (ie_eclipse_type_w = 'OVS') then
			CALL get_ovs_claim(nr_interno_conta_w, c01_w.cd_estabelecimento, nm_usuario_p, null, 'Y');
		end if;

	end if;

	end;
	end loop;
	close c01;

	select		count(a.nr_sequencia) qt_inconsistencies
	into STRICT    	qt_inconsistencies_w
	from    	eclipse_inco_account a,
				conta_paciente b
	where   	a.nr_interno_conta = b.nr_interno_conta
	and     	b.nr_seq_protocolo = nr_seq_protocolo_p;


	if (qt_inconsistencies_w IS NOT NULL AND qt_inconsistencies_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1106787, 'QT_INCONSISTENCIA=' || qt_inconsistencies_w);
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
