CREATE OR REPLACE PROCEDURE efd_gerar_nr_seq_item (dt_referencia_p timestamp) AS $body$
DECLARE


nr_sequencia_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	nota_fiscal
	where	trunc(dt_emissao,'mm') = trunc(dt_referencia_p,'mm');


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL gerar_nr_sequencial_nf_item(nr_sequencia_w);
	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
