CREATE OR REPLACE PROCEDURE efd_liberar_arquivo ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text ) AS $body$
BEGIN

if (ie_opcao_p = 'L') then
	begin
	update 	fis_efd_controle
	set	dt_liberacao = clock_timestamp(),
		nm_usuario_lib = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;
	end;
else if (ie_opcao_p = 'E') then
	begin
	update 	fis_efd_controle
	set	dt_liberacao  = NULL,
		nm_usuario_lib = ''
	where	nr_sequencia = nr_sequencia_p;
	end;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
