CREATE OR REPLACE PROCEDURE efetuar_rep_tecnica_proc ( nr_prescricao_p bigint, nr_seq_proced_p bigint, ds_observacao_p text, nm_usuario_p text ) AS $body$
DECLARE


ds_observacao_w	varchar(255);

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_proced_p IS NOT NULL AND nr_seq_proced_p::text <> '') then
	begin
	ds_observacao_w := substr(ds_observacao_p,1,255);

	update	prescr_procedimento
	set	dt_reprovacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		ds_justificativa_reprov = ds_observacao_w
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_proced_p
	and	coalesce(ie_exige_liberacao,'N') = 'S';

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
