CREATE OR REPLACE PROCEDURE egk_tratar_conv_existentes (cd_pessoa_fisica_p text, cd_convenio_p bigint, dt_fim_vigencia_p timestamp) AS $body$
BEGIN

if (obter_tipo_convenio(cd_convenio_p) = 11) then
	update	pessoa_titular_convenio
	set	dt_fim_vigencia = dt_fim_vigencia_p
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	obter_tipo_convenio(cd_convenio) = 11
	and	cd_convenio <> cd_convenio_p
	and	coalesce(dt_fim_vigencia::text, '') = '';
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
