SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ehr_gerar_reg_template ( nr_sequencia_p bigint, nr_seq_template_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_seq_reg_template_w	bigint;

qt_reg_w	bigint;
nr_seq_template_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0),
	max(nr_seq_template)
into STRICT	nr_seq_reg_template_w,
	nr_seq_template_w
from	ehr_reg_template
where	nr_seq_reg	= nr_sequencia_p
and	(nr_seq_template IS NOT NULL AND nr_seq_template::text <> '');


if (nr_seq_reg_template_w	= 0) then

	select	nextval('ehr_reg_template_seq')
	into STRICT	nr_seq_reg_template_w
	;

	insert into ehr_reg_template(
		nr_sequencia,
		nr_seq_reg,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_registro,
		nr_seq_template,
		nr_seq_item_pep,
		dt_liberacao,
		nr_seq_apres)
	values (
		nr_seq_reg_template_w,
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_template_p,
		null,
		null,
		null);
elsif (nr_seq_template_w	<> nr_seq_template_p) and (nr_seq_template_p IS NOT NULL AND nr_seq_template_p::text <> '')then
	update	ehr_reg_template
	set	nr_seq_template	= nr_seq_template_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_reg_template_w;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
