CREATE OR REPLACE PROCEDURE ehr_inativar_reg_transplante ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_usuario_w	varchar(15);


BEGIN
select	b.nm_usuario
into STRICT	nm_usuario_w
from	tx_liberacao_lista b
where	b.nr_sequencia =   nr_sequencia_p;

if (nm_usuario_w = nm_usuario_p) then
	update	tx_liberacao_lista
	set	ie_situacao = 'I',
		nm_usuario_inativacao = nm_usuario_p,
		dt_inativacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
else
	CALL Wheb_mensagem_pck.exibir_mensagem_abort( 262328 , 'NM_USUARIO_W='||nm_usuario_w);
end if;

commit;

end;
