CREATE OR REPLACE PROCEDURE ehr_inativar_template (nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


qt_nao_lib_w		bigint;


BEGIN

if (ie_opcao_p = 'I') then /*Inativar*/
	begin
	update	ehr_template
	set	dt_inativacao		= clock_timestamp(),
		nm_usuario_inativ	= nm_usuario_p,
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_sequencia_p;
	end;
elsif (ie_opcao_p = 'RI') then /*Reverter inativação*/
	begin
	update	ehr_template
	set	dt_inativacao		 = NULL,
		nm_usuario_inativ	 = NULL,
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_sequencia_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
