CREATE OR REPLACE PROCEDURE ehr_inform_liberar_sae_js ( nr_seq_pep_prescr_p bigint, nm_usuario_p text, qt_pe_material_proced_p INOUT bigint, qt_pe_interv_protocolo_p INOUT bigint, qt_pe_prescr_proc_p INOUT bigint, qt_pe_prescricao_erro_p INOUT bigint, ds_per_gerar_prescr_sae_p INOUT text, ds_per_lib_prescr_sae_p INOUT text, ds_per_lib_sae_p INOUT text, ds_mens_interve_sae_p INOUT text ) AS $body$
DECLARE


qt_pe_material_proced_w	bigint := 0;
qt_pe_interv_protocolo_w	bigint := 0;
qt_pe_prescr_proc_w	bigint := 0;
qt_pe_prescricao_erro_w	bigint := 0;
ds_per_gerar_prescr_sae_w	varchar(255);
ds_per_lib_prescr_sae_w	varchar(255);
ds_per_lib_sae_w		varchar(255);
ds_mens_interve_sae_w	varchar(255);


BEGIN

if (nr_seq_pep_prescr_p IS NOT NULL AND nr_seq_pep_prescr_p::text <> '') then
	begin
	select 	count(*)
	into STRICT	qt_pe_material_proced_w
	from	pe_prescr_proc a,
		pe_material_proced b
	where	b.nr_seq_proc	= a.nr_seq_proc
	and	a.nr_seq_prescr	= nr_seq_pep_prescr_p;

	select	count(*)
	into STRICT	qt_pe_interv_protocolo_w
	from	pe_prescr_proc b,
		pe_interv_protocolo a
	where	b.nr_seq_proc	= a.nr_seq_interv
	and	b.nr_seq_prescr	= nr_seq_pep_prescr_p;

	select	count(*)
	into STRICT	qt_pe_prescr_proc_w
	from	pe_prescr_proc
	where	nr_seq_prescr = nr_seq_pep_prescr_p;
	end;

	select	count(*)
	into STRICT	qt_pe_prescricao_erro_w
	from	pe_prescricao_erro
	where	nr_seq_prescr = nr_seq_pep_prescr_p
	and	ie_libera = 'N';

end if;

-- Deseja gerar uma prescrição para este SAE?
ds_per_gerar_prescr_sae_w := substr(obter_texto_tasy(59525, wheb_usuario_pck.get_nr_seq_idioma),1,255);

-- Deseja liberar a Prescrição deste SAE?
ds_per_lib_prescr_sae_w	 := substr(obter_texto_tasy(59526, wheb_usuario_pck.get_nr_seq_idioma),1,255);

-- Deseja liberar esta SAE?
ds_per_lib_sae_w	 := substr(obter_texto_tasy(59527, wheb_usuario_pck.get_nr_seq_idioma),1,255);

-- Não existe nenhuma intervenção cadastrada para o SAE. [399]
ds_mens_interve_sae_w	 := substr(obter_texto_tasy(59528, wheb_usuario_pck.get_nr_seq_idioma),1,255);

qt_pe_material_proced_p	:= qt_pe_material_proced_w;
qt_pe_interv_protocolo_p	:= qt_pe_interv_protocolo_w;
qt_pe_prescr_proc_p	:= qt_pe_prescr_proc_w;
qt_pe_prescricao_erro_p	:= qt_pe_prescricao_erro_w;
ds_per_gerar_prescr_sae_p	:= ds_per_gerar_prescr_sae_w;
ds_per_lib_prescr_sae_p	:= ds_per_lib_prescr_sae_w;
ds_per_lib_sae_p		:= ds_per_lib_sae_w;
ds_mens_interve_sae_p	:= ds_mens_interve_sae_w;

end;
$body$
