CREATE OR REPLACE PROCEDURE ehr_registro_afterpost ( nr_seq_item_perfil_p bigint, qt_template_p INOUT bigint, nr_seq_registro_p bigint, ds_lista_templates_p text, nm_usuario_p text) AS $body$
DECLARE


qt_template_w	bigint := 0;


BEGIN

select	count(*)
into STRICT	qt_template_w
from	perfil_item_pepo_template
