CREATE OR REPLACE PROCEDURE eis_ajustar_indicadores () AS $body$
DECLARE


nr_sequencia_sup_w	bigint;
ds_indicador_sup_w	varchar(255);
nr_seq_wheb_sup_w	bigint;
ie_situacao_sup_w	varchar(1);
ds_sql_where_sup_w	varchar(255);
ds_tabela_visao_sup_w	varchar(255);

nr_sequencia_w		bigint;
ds_indicador_w		varchar(255);
nr_seq_wheb_w		bigint;
ie_situacao_w		varchar(1);
ds_sql_where_w		varchar(255);
ds_tabela_visao_w	varchar(255);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_indicador,
		nr_seq_wheb,
		ie_situacao,
		ds_sql_where,
		ds_tabela_visao
	from indicador_gestao
	order by 2,1,3;


BEGIN

open c01;
loop
fetch c01 into	nr_sequencia_w,
                ds_indicador_w,
                nr_seq_wheb_w,
                ie_situacao_w,
                ds_sql_where_w,
		ds_tabela_visao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if	(ds_indicador_w = ds_indicador_sup_w and
		((nr_seq_wheb_w <> nr_seq_wheb_sup_w) or (coalesce(nr_seq_wheb_w::text, '') = '' and coalesce(nr_seq_wheb_sup_w::text, '') = '')) and
		ie_situacao_w = ie_situacao_sup_w and
		((ds_sql_where_w = ds_sql_where_sup_w) or (coalesce(ds_sql_where_w::text, '') = '' and coalesce(ds_sql_where_sup_w::text, '') = '')) and
		ds_tabela_visao_w = ds_tabela_visao_sup_w) then

		update	indicador_gestao
		set	ie_situacao = 'I'
		where	nr_sequencia = nr_sequencia_w;

	end if;

	CALL eis_ajustar_indicadores_atrib(nr_sequencia_w);

	nr_sequencia_sup_w := nr_sequencia_w;
	ds_indicador_sup_w := ds_indicador_w;
	nr_seq_wheb_sup_w :=  nr_seq_wheb_w;
	ie_situacao_sup_w :=  ie_situacao_w;
	ds_sql_where_sup_w := ds_sql_where_w;
	ds_tabela_visao_sup_w := ds_tabela_visao_w;

	end;
end loop;
close c01;

commit;

end;
