CREATE OR REPLACE PROCEDURE eis_cirurgia_mensal (dt_inicio_mes_p timestamp, dt_final_mes_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
dt_atual_w	timestamp;
nr_sequencia_w			bigint;
ds_mensagem_w			varchar(2000) := null;
ds_mensagem_ww			varchar(2000) := null;
expressao1_w	varchar(255) := obter_desc_expressao_idioma(284979, null, wheb_usuario_pck.get_nr_seq_idioma);--Cirurgia 
BEGIN 
 
 
nr_sequencia_w := Gravar_Log_Indicador(11, expressao1_w, clock_timestamp(), trunc(dt_inicio_mes_p), nm_usuario_p, nr_sequencia_w);
 
DELETE	FROM eis_cirurgia 
WHERE 	TRUNC(dt_referencia, 'month') 	= TRUNC(dt_inicio_mes_p, 'month') 
AND	ie_periodo	= 'M';
 
DELETE FROM eis_cirurgia 
WHERE 	TRUNC(dt_referencia, 'year') = TRUNC(dt_inicio_mes_p, 'year') 
AND	ie_periodo	= 'A';
 
dt_atual_w	:= dt_inicio_mes_p;
 
WHILE(dt_atual_w <= dt_final_mes_p)LOOP 
	BEGIN 
	ds_mensagem_w := Gerar_Eis_Cir_Diaria(dt_atual_w, nm_usuario_p, ds_mensagem_w);
 
	if ((coalesce(ds_mensagem_ww,'XPTO') <> 'XPTO') and (coalesce(ds_mensagem_w,'XPTO') <> 'XPTO')) then 
		ds_mensagem_ww := substr(ds_mensagem_ww || '; ',1,2000);
	end if;
	 
	if (coalesce(ds_mensagem_w,'XPTO') <> 'XPTO') then 
		ds_mensagem_ww := substr(ds_mensagem_ww || ds_mensagem_w,1,2000);
	end if;
 
	dt_atual_w	:= dt_atual_w + 1;
 
	END;
END LOOP;
 
