CREATE OR REPLACE PROCEDURE eis_gerar_auditoria_mensal (dt_inicio_mes_p timestamp, dt_final_mes_p timestamp, cd_estabelecimento_P bigint, nm_usuario_p text) AS $body$
DECLARE


dt_atual_w	timestamp;


BEGIN

dt_atual_w	:= dt_inicio_mes_p;

while(dt_atual_w <= dt_final_mes_p)loop
	begin

	if (dt_atual_w <= trunc(clock_timestamp(),'dd')) then
		CALL gerar_eis_auditoria(dt_atual_w, cd_estabelecimento_p, nm_usuario_p);
	end if;

	dt_atual_w	:= dt_atual_w + 1;

	end;
end loop;

end;
$body$
